import { Injectable } from '@angular/core';
import { HttpClientModule } from '@angular/common/http';
import { HttpClient, HttpHeaders } from '@angular/common/http';

/*
 * Behavior subject
 */
import { BehaviorSubject } from 'rxjs';
import { Observable, of } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';

import { SimulationAPI } from './simulation-api';
import { SimulationDB } from './simulation-db';

@Injectable({
  providedIn: 'root'
})
export class SimulationService {

  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
  };

  // Kamar Ra√Ømo: Adding a way to communicate between components that share information
  // so that they are notified of a change.
  SimulationServiceChanged: BehaviorSubject<string> = new BehaviorSubject("");

  private simulationsUrl = 'http://localhost:8080/api/github.com/fullstack-lang/gong/examples/laundromat/go/v1/simulations';

  constructor(
    private http: HttpClient
  ) { }

  /** GET simulations from the server */
  getSimulations(): Observable<SimulationDB[]> {
    return this.http.get<SimulationDB[]>(this.simulationsUrl)
      .pipe(
        tap(_ => this.log('fetched simulations')),
        catchError(this.handleError<SimulationDB[]>('getSimulations', []))
      );
  }

  /** GET simulation by id. Will 404 if id not found */
  getSimulation(id: number): Observable<SimulationDB> {
    const url = `${this.simulationsUrl}/${id}`;
    return this.http.get<SimulationDB>(url).pipe(
      tap(_ => this.log(`fetched simulation id=${id}`)),
      catchError(this.handleError<SimulationDB>(`getSimulation id=${id}`))
    );
  }

  //////// Save methods //////////

  /** POST: add a new simulation to the server */
  postSimulation(simulationAPI: SimulationAPI): Observable<SimulationDB> {
    return this.http.post<SimulationDB>(this.simulationsUrl, simulationAPI, this.httpOptions).pipe(
      tap((newSimulation: SimulationDB) => {})
    );
  }

  /** DELETE: delete the simulationdb from the server */
  deleteSimulation(simulationdb: SimulationDB | number): Observable<SimulationDB> {
    const id = typeof simulationdb === 'number' ? simulationdb : simulationdb.ID;
    const url = `${this.simulationsUrl}/${id}`;

    return this.http.delete<SimulationDB>(url, this.httpOptions).pipe(
      tap(_ => this.log(`deleted simulationdb id=${id}`)),
      catchError(this.handleError<SimulationDB>('deleteSimulation'))
    );
  }

  /** PUT: update the simulationdb on the server */
  updateSimulation(simulationdb: SimulationDB): Observable<SimulationDB> {
    const id = typeof simulationdb === 'number' ? simulationdb : simulationdb.ID;
    const url = `${this.simulationsUrl}/${id}`;

    // insertion point for reset of reverse pointers (to avoid circular JSON)
    simulationdb.Machine = {}
    simulationdb.Washer = {}

    return this.http.put(url, simulationdb, this.httpOptions).pipe(
      tap(_ => {
        // insertion point for restoration of reverse pointers
        this.log(`updated simulationdb id=${simulationdb.ID}`)
      }),
      catchError(this.handleError<SimulationDB>('updateSimulation'))
    );
  }

  /**
   * Handle Http operation that failed.
   * Let the app continue.
   * @param operation - name of the operation that failed
   * @param result - optional value to return as the observable result
   */
  private handleError<T>(operation = 'operation', result?: T) {
    return (error: any): Observable<T> => {

      // TODO: send the error to remote logging infrastructure
      console.error(error); // log to console instead

      // TODO: better job of transforming error for user consumption
      this.log(`${operation} failed: ${error.message}`);

      // Let the app keep running by returning an empty result.
      return of(result as T);
    };
  }

  private log(message: string) {

  }
}
