		_, err := backRepoBar.db.Create(barDB)
		if err != nil {
			log.Fatal(err)
		}
		backRepoBar.Map_BarDBID_BarDB[barDB.ID] = barDB
		BackRepoBarid_atBckpTime_newID[barDB_ID_atBackupTime] = barDB.ID
