import { Component, OnInit, Inject, Optional } from '@angular/core';
import { TypeofExpr } from '@angular/compiler';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { GongEnumDB } from '../gongenum-db'
import { GongEnumService } from '../gongenum.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'
import { NullInt64 } from '../null-int64'

@Component({
  selector: 'lib-gongenum-sorting',
  templateUrl: './gongenum-sorting.component.html',
  styleUrls: ['./gongenum-sorting.component.css']
})
export class GongEnumSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of GongEnum instances that are in the association
  associatedGongEnums = new Array<GongEnumDB>();

  constructor(
    private gongenumService: GongEnumService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of gongenum instances
    public dialogRef: MatDialogRef<GongEnumSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getGongEnums()
  }

  getGongEnums(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let gongenum of this.frontRepo.GongEnums_array) {
          let ID = this.dialogData.ID
          let revPointerID = gongenum[this.dialogData.ReversePointer as keyof GongEnumDB] as unknown as NullInt64
          let revPointerID_Index = gongenum[this.dialogData.ReversePointer + "_Index" as keyof GongEnumDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedGongEnums.push(gongenum)
          }
        }

        // sort associated gongenum according to order
        this.associatedGongEnums.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedGongEnums, event.previousIndex, event.currentIndex);

    // set the order of GongEnum instances
    let index = 0

    for (let gongenum of this.associatedGongEnums) {
      let revPointerID_Index = gongenum[this.dialogData.ReversePointer + "_Index" as keyof GongEnumDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedGongEnums.forEach(
      gongenum => {
        this.gongenumService.updateGongEnum(gongenum)
          .subscribe(gongenum => {
            this.gongenumService.GongEnumServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer +' done');
  }
}
