import { Component, OnInit, Inject, Optional } from '@angular/core';
import { TypeofExpr } from '@angular/compiler';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { GongLinkDB } from '../gonglink-db'
import { GongLinkService } from '../gonglink.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'
import { NullInt64 } from '../null-int64'

@Component({
  selector: 'lib-gonglink-sorting',
  templateUrl: './gonglink-sorting.component.html',
  styleUrls: ['./gonglink-sorting.component.css']
})
export class GongLinkSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of GongLink instances that are in the association
  associatedGongLinks = new Array<GongLinkDB>();

  constructor(
    private gonglinkService: GongLinkService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of gonglink instances
    public dialogRef: MatDialogRef<GongLinkSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getGongLinks()
  }

  getGongLinks(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let gonglink of this.frontRepo.GongLinks_array) {
          let ID = this.dialogData.ID
          let revPointerID = gonglink[this.dialogData.ReversePointer as keyof GongLinkDB] as unknown as NullInt64
          let revPointerID_Index = gonglink[this.dialogData.ReversePointer + "_Index" as keyof GongLinkDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedGongLinks.push(gonglink)
          }
        }

        // sort associated gonglink according to order
        this.associatedGongLinks.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedGongLinks, event.previousIndex, event.currentIndex);

    // set the order of GongLink instances
    let index = 0

    for (let gonglink of this.associatedGongLinks) {
      let revPointerID_Index = gonglink[this.dialogData.ReversePointer + "_Index" as keyof GongLinkDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedGongLinks.forEach(
      gonglink => {
        this.gonglinkService.updateGongLink(gonglink)
          .subscribe(gonglink => {
            this.gonglinkService.GongLinkServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer +' done');
  }
}
