// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { GongNoteDB } from '../gongnote-db'
import { GongNoteService } from '../gongnote.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-gongnotestable',
  templateUrl: './gongnotes-table.component.html',
  styleUrls: ['./gongnotes-table.component.css'],
})
export class GongNotesTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of GongNote instances
  selection: SelectionModel<GongNoteDB> = new (SelectionModel)
  initialSelection = new Array<GongNoteDB>()

  // the data source for the table
  gongnotes: GongNoteDB[] = []
  matTableDataSource: MatTableDataSource<GongNoteDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.gongnotes
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (gongnoteDB: GongNoteDB, property: string) => {
      switch (property) {
        case 'ID':
          return gongnoteDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return gongnoteDB.Name;

        case 'Body':
          return gongnoteDB.Body;

        case 'BodyHTML':
          return gongnoteDB.BodyHTML;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (gongnoteDB: GongNoteDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the gongnoteDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += gongnoteDB.Name.toLowerCase()
      mergedContent += gongnoteDB.Body.toLowerCase()
      mergedContent += gongnoteDB.BodyHTML.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private gongnoteService: GongNoteService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of gongnote instances
    public dialogRef: MatDialogRef<GongNotesTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.gongnoteService.GongNoteServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getGongNotes()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Body",
        "BodyHTML",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Body",
        "BodyHTML",
      ]
      this.selection = new SelectionModel<GongNoteDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getGongNotes()

    this.matTableDataSource = new MatTableDataSource(this.gongnotes)
  }

  getGongNotes(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.gongnotes = this.frontRepo.GongNotes_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let gongnote of this.gongnotes) {
            let ID = this.dialogData.ID
            let revPointer = gongnote[this.dialogData.ReversePointer as keyof GongNoteDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(gongnote)
            }
            this.selection = new SelectionModel<GongNoteDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, GongNoteDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to GongNoteDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as GongNoteDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let gongnote = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as GongNoteDB
              this.initialSelection.push(gongnote)
            }
          }

          this.selection = new SelectionModel<GongNoteDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.gongnotes
      }
    )
  }

  // newGongNote initiate a new gongnote
  // create a new GongNote objet
  newGongNote() {
  }

  deleteGongNote(gongnoteID: number, gongnote: GongNoteDB) {
    // list of gongnotes is truncated of gongnote before the delete
    this.gongnotes = this.gongnotes.filter(h => h !== gongnote);

    this.gongnoteService.deleteGongNote(gongnoteID, this.GONG__StackPath).subscribe(
      gongnote => {
        this.gongnoteService.GongNoteServiceChanged.next("delete")
      }
    );
  }

  editGongNote(gongnoteID: number, gongnote: GongNoteDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(gongnoteID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "gongnote" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, gongnoteID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.gongnotes.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.gongnotes.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<GongNoteDB>()

      // reset all initial selection of gongnote that belong to gongnote
      for (let gongnote of this.initialSelection) {
        let index = gongnote[this.dialogData.ReversePointer as keyof GongNoteDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(gongnote)

      }

      // from selection, set gongnote that belong to gongnote
      for (let gongnote of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = gongnote[this.dialogData.ReversePointer as keyof GongNoteDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(gongnote)
      }


      // update all gongnote (only update selection & initial selection)
      for (let gongnote of toUpdate) {
        this.gongnoteService.updateGongNote(gongnote, this.GONG__StackPath)
          .subscribe(gongnote => {
            this.gongnoteService.GongNoteServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, GongNoteDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedGongNote = new Set<number>()
      for (let gongnote of this.initialSelection) {
        if (this.selection.selected.includes(gongnote)) {
          // console.log("gongnote " + gongnote.Name + " is still selected")
        } else {
          console.log("gongnote " + gongnote.Name + " has been unselected")
          unselectedGongNote.add(gongnote.ID)
          console.log("is unselected " + unselectedGongNote.has(gongnote.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let gongnote = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as GongNoteDB
      if (unselectedGongNote.has(gongnote.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<GongNoteDB>) = new Array<GongNoteDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          gongnote => {
            if (!this.initialSelection.includes(gongnote)) {
              // console.log("gongnote " + gongnote.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + gongnote.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = gongnote.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = gongnote.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("gongnote " + gongnote.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<GongNoteDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
