// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-modelpkgstable',
  templateUrl: './modelpkgs-table.component.html',
  styleUrls: ['./modelpkgs-table.component.css'],
})
export class ModelPkgsTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of ModelPkg instances
  selection: SelectionModel<ModelPkgDB> = new (SelectionModel)
  initialSelection = new Array<ModelPkgDB>()

  // the data source for the table
  modelpkgs: ModelPkgDB[] = []
  matTableDataSource: MatTableDataSource<ModelPkgDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.modelpkgs
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (modelpkgDB: ModelPkgDB, property: string) => {
      switch (property) {
        case 'ID':
          return modelpkgDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return modelpkgDB.Name;

        case 'PkgPath':
          return modelpkgDB.PkgPath;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (modelpkgDB: ModelPkgDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the modelpkgDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += modelpkgDB.Name.toLowerCase()
      mergedContent += modelpkgDB.PkgPath.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private modelpkgService: ModelPkgService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of modelpkg instances
    public dialogRef: MatDialogRef<ModelPkgsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.modelpkgService.ModelPkgServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getModelPkgs()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "PkgPath",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "PkgPath",
      ]
      this.selection = new SelectionModel<ModelPkgDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getModelPkgs()

    this.matTableDataSource = new MatTableDataSource(this.modelpkgs)
  }

  getModelPkgs(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.modelpkgs = this.frontRepo.ModelPkgs_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let modelpkg of this.modelpkgs) {
            let ID = this.dialogData.ID
            let revPointer = modelpkg[this.dialogData.ReversePointer as keyof ModelPkgDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(modelpkg)
            }
            this.selection = new SelectionModel<ModelPkgDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, ModelPkgDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to ModelPkgDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as ModelPkgDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let modelpkg = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as ModelPkgDB
              this.initialSelection.push(modelpkg)
            }
          }

          this.selection = new SelectionModel<ModelPkgDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.modelpkgs
      }
    )
  }

  // newModelPkg initiate a new modelpkg
  // create a new ModelPkg objet
  newModelPkg() {
  }

  deleteModelPkg(modelpkgID: number, modelpkg: ModelPkgDB) {
    // list of modelpkgs is truncated of modelpkg before the delete
    this.modelpkgs = this.modelpkgs.filter(h => h !== modelpkg);

    this.modelpkgService.deleteModelPkg(modelpkgID, this.GONG__StackPath).subscribe(
      modelpkg => {
        this.modelpkgService.ModelPkgServiceChanged.next("delete")
      }
    );
  }

  editModelPkg(modelpkgID: number, modelpkg: ModelPkgDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(modelpkgID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "modelpkg" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, modelpkgID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.modelpkgs.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.modelpkgs.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<ModelPkgDB>()

      // reset all initial selection of modelpkg that belong to modelpkg
      for (let modelpkg of this.initialSelection) {
        let index = modelpkg[this.dialogData.ReversePointer as keyof ModelPkgDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(modelpkg)

      }

      // from selection, set modelpkg that belong to modelpkg
      for (let modelpkg of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = modelpkg[this.dialogData.ReversePointer as keyof ModelPkgDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(modelpkg)
      }


      // update all modelpkg (only update selection & initial selection)
      for (let modelpkg of toUpdate) {
        this.modelpkgService.updateModelPkg(modelpkg, this.GONG__StackPath)
          .subscribe(modelpkg => {
            this.modelpkgService.ModelPkgServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, ModelPkgDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedModelPkg = new Set<number>()
      for (let modelpkg of this.initialSelection) {
        if (this.selection.selected.includes(modelpkg)) {
          // console.log("modelpkg " + modelpkg.Name + " is still selected")
        } else {
          console.log("modelpkg " + modelpkg.Name + " has been unselected")
          unselectedModelPkg.add(modelpkg.ID)
          console.log("is unselected " + unselectedModelPkg.has(modelpkg.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let modelpkg = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as ModelPkgDB
      if (unselectedModelPkg.has(modelpkg.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<ModelPkgDB>) = new Array<ModelPkgDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          modelpkg => {
            if (!this.initialSelection.includes(modelpkg)) {
              // console.log("modelpkg " + modelpkg.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + modelpkg.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = modelpkg.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = modelpkg.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("modelpkg " + modelpkg.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<ModelPkgDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
