  ngOnInit(): void {
    this.getEstructs()
  }

  getEstructs(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let estruct of this.frontRepo.Estructs_array) {
          let ID = this.dialogData.ID
          let revPointerID = estruct[this.dialogData.ReversePointer as keyof EstructDB] as unknown as NullInt64
          let revPointerID_Index = estruct[this.dialogData.ReversePointer + "_Index" as keyof EstructDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedEstructs.push(estruct)
          }
        }

        // sort associated estruct according to order
        this.associatedEstructs.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedEstructs, event.previousIndex, event.currentIndex);

    // set the order of Estruct instances
    let index = 0

    for (let estruct of this.associatedEstructs) {
      let revPointerID_Index = estruct[this.dialogData.ReversePointer + "_Index" as keyof EstructDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedEstructs.forEach(
      estruct => {
        this.estructService.updateEstruct(estruct)
          .subscribe(estruct => {
            this.estructService.EstructServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer +' done');
  }
