  ngOnInit(): void {
    this.getAclassBclassUses()
  }

  getAclassBclassUses(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let aclassbclassuse of this.frontRepo.AclassBclassUses_array) {
          let ID = this.dialogData.ID
          let revPointerID = aclassbclassuse[this.dialogData.ReversePointer]
          let revPointerID_Index = aclassbclassuse[this.dialogData.ReversePointer+"_Index"]
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedAclassBclassUses.push(aclassbclassuse)
          }
        }

        // sort associated aclassbclassuse according to order
        this.associatedAclassBclassUses.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer+"_Index"]
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer+"_Index"]
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }  
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedAclassBclassUses, event.previousIndex, event.currentIndex);

    // set the order of AclassBclassUse instances
    let index = 0
    
    for (let aclassbclassuse of this.associatedAclassBclassUses) {
      let revPointerID_Index = aclassbclassuse[this.dialogData.ReversePointer+"_Index"]
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedAclassBclassUses.forEach(
      aclassbclassuse => {
        this.aclassbclassuseService.updateAclassBclassUse(aclassbclassuse)
          .subscribe(aclassbclassuse => {
            this.aclassbclassuseService.AclassBclassUseServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of '+ this.dialogData.ReversePointer+' done');
  }
