import { Component, OnInit, Inject, Optional } from '@angular/core';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { AstructDB } from '../astruct-db'
import { AstructService } from '../astruct.service'

import { FrontRepoService, FrontRepo, NullInt64 } from '../front-repo.service'
@Component({
  selector: 'lib-astruct-sorting',
  templateUrl: './astruct-sorting.component.html',
  styleUrls: ['./astruct-sorting.component.css']
})
export class AstructSortingComponent implements OnInit {

  frontRepo: FrontRepo

  // array of Astruct instances that are in the association
  associatedAstructs = new Array<AstructDB>();

  constructor(
    private astructService: AstructService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of astruct instances
    public dialogRef: MatDialogRef<AstructSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getAstructs()
  }

  getAstructs(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let astruct of this.frontRepo.Astructs_array) {
          let ID = this.dialogData.ID
          let revPointerID = astruct[this.dialogData.ReversePointer]
          let revPointerID_Index = astruct[this.dialogData.ReversePointer+"_Index"]
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedAstructs.push(astruct)
          }
        }

        // sort associated astruct according to order
        this.associatedAstructs.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer+"_Index"]
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer+"_Index"]
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }  
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedAstructs, event.previousIndex, event.currentIndex);

    // set the order of Astruct instances
    let index = 0
    
    for (let astruct of this.associatedAstructs) {
      let revPointerID_Index = astruct[this.dialogData.ReversePointer+"_Index"]
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedAstructs.forEach(
      astruct => {
        this.astructService.updateAstruct(astruct)
          .subscribe(astruct => {
            this.astructService.AstructServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of '+ this.dialogData.ReversePointer+' done');
  }
}
