// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { BclassDB } from '../bclass-db'
import { BclassService } from '../bclass.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-bclassstable',
  templateUrl: './bclasss-table.component.html',
  styleUrls: ['./bclasss-table.component.css'],
})
export class BclasssTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode

  // used if the component is called as a selection component of Bclass instances
  selection: SelectionModel<BclassDB>;
  initialSelection = new Array<BclassDB>();

  // the data source for the table
  bclasss: BclassDB[];
  matTableDataSource: MatTableDataSource<BclassDB>


  // front repo, that will be referenced by this.bclasss
  frontRepo: FrontRepo

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort) sort: MatSort;
  @ViewChild(MatPaginator) paginator: MatPaginator;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (bclassDB: BclassDB, property: string) => {
      switch (property) {
        // insertion point for specific sorting accessor
        case 'Name':
          return bclassDB.Name;

        case 'Floatfield':
          return bclassDB.Floatfield;

        case 'Intfield':
          return bclassDB.Intfield;

        case 'Anarrayofb':
          return this.frontRepo.Aclasss.get(bclassDB.Aclass_AnarrayofbDBID.Int64)?.Name;

        case 'Anotherarrayofb':
          return this.frontRepo.Aclasss.get(bclassDB.Aclass_AnotherarrayofbDBID.Int64)?.Name;

        default:
          return BclassDB[property];
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (bclassDB: BclassDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the bclassDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += bclassDB.Name.toLowerCase()
      mergedContent += bclassDB.Floatfield.toString()
      mergedContent += bclassDB.Intfield.toString()
      if (bclassDB.Aclass_AnarrayofbDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Aclasss.get(bclassDB.Aclass_AnarrayofbDBID.Int64)?.Name.toLowerCase()
      }

      if (bclassDB.Aclass_AnotherarrayofbDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Aclasss.get(bclassDB.Aclass_AnotherarrayofbDBID.Int64)?.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort;
    this.matTableDataSource.paginator = this.paginator;
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private bclassService: BclassService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of bclass instances
    public dialogRef: MatDialogRef<BclasssTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
    }

    // observable for changes in structs
    this.bclassService.BclassServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getBclasss()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
		this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "Floatfield",
        "Intfield",
        "Anarrayofb",
        "Anotherarrayofb",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Floatfield",
        "Intfield",
        "Anarrayofb",
        "Anotherarrayofb",
      ]
      this.selection = new SelectionModel<BclassDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getBclasss()
    this.matTableDataSource = new MatTableDataSource(this.bclasss)
  }

  getBclasss(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.bclasss = this.frontRepo.Bclasss_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
			this.bclasss.forEach(
            bclass => {
              let ID = this.dialogData.ID
              let revPointer = bclass[this.dialogData.ReversePointer]
              if (revPointer.Int64 == ID) {
                this.initialSelection.push(bclass)
              }
            }
          )
          this.selection = new SelectionModel<BclassDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.bclasss
      }
    )
  }

  // newBclass initiate a new bclass
  // create a new Bclass objet
  newBclass() {
  }

  deleteBclass(bclassID: number, bclass: BclassDB) {
    // list of bclasss is truncated of bclass before the delete
    this.bclasss = this.bclasss.filter(h => h !== bclass);

    this.bclassService.deleteBclass(bclassID).subscribe(
      bclass => {
        this.bclassService.BclassServiceChanged.next("delete")
      }
    );
  }

  editBclass(bclassID: number, bclass: BclassDB) {

  }

  // display bclass in router
  displayBclassInRouter(bclassID: number) {
    this.router.navigate(["github_com_fullstack_lang_gong_test_go-" + "bclass-display", bclassID])
  }

  // set editor outlet
  setEditorRouterOutlet(bclassID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gong_test_go_editor: ["github_com_fullstack_lang_gong_test_go-" + "bclass-detail", bclassID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(bclassID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gong_test_go_presentation: ["github_com_fullstack_lang_gong_test_go-" + "bclass-presentation", bclassID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.bclasss.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.bclasss.forEach(row => this.selection.select(row));
  }

  save() {

    let toUpdate = new Set<BclassDB>()

    // reset all initial selection of bclass that belong to bclass through Anarrayofb
    this.initialSelection.forEach(
      bclass => {
        bclass[this.dialogData.ReversePointer].Int64 = 0
        bclass[this.dialogData.ReversePointer].Valid = true
        toUpdate.add(bclass)
      }
    )

    // from selection, set bclass that belong to bclass through Anarrayofb
    this.selection.selected.forEach(
      bclass => {
        let ID = +this.dialogData.ID
        bclass[this.dialogData.ReversePointer].Int64 = ID
        bclass[this.dialogData.ReversePointer].Valid = true
        toUpdate.add(bclass)
      }
    )

    // update all bclass (only update selection & initial selection)
    toUpdate.forEach(
      bclass => {
        this.bclassService.updateBclass(bclass)
          .subscribe(bclass => {
            this.bclassService.BclassServiceChanged.next("update")
          });
      }
    )
    this.dialogRef.close('Pizza!');
  }
}
