// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, NullInt64, SelectionMode } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { BstructDB } from '../bstruct-db'
import { BstructService } from '../bstruct.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-bstructstable',
  templateUrl: './bstructs-table.component.html',
  styleUrls: ['./bstructs-table.component.css'],
})
export class BstructsTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode

  // used if the component is called as a selection component of Bstruct instances
  selection: SelectionModel<BstructDB>;
  initialSelection = new Array<BstructDB>();

  // the data source for the table
  bstructs: BstructDB[];
  matTableDataSource: MatTableDataSource<BstructDB>

  // front repo, that will be referenced by this.bstructs
  frontRepo: FrontRepo

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort) sort: MatSort;
  @ViewChild(MatPaginator) paginator: MatPaginator;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (bstructDB: BstructDB, property: string) => {
      switch (property) {
        // insertion point for specific sorting accessor
        case 'Name':
          return bstructDB.Name;

        case 'Floatfield':
          return bstructDB.Floatfield;

        case 'Intfield':
          return bstructDB.Intfield;

        case 'Astruct_Anarrayofb':
          return this.frontRepo.Astructs.get(bstructDB.Astruct_AnarrayofbDBID.Int64)?.Name;

        case 'Astruct_Anotherarrayofb':
          return this.frontRepo.Astructs.get(bstructDB.Astruct_AnotherarrayofbDBID.Int64)?.Name;

        default:
          return BstructDB[property];
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (bstructDB: BstructDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the bstructDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += bstructDB.Name.toLowerCase()
      mergedContent += bstructDB.Floatfield.toString()
      mergedContent += bstructDB.Intfield.toString()
      if (bstructDB.Astruct_AnarrayofbDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Astructs.get(bstructDB.Astruct_AnarrayofbDBID.Int64)?.Name.toLowerCase()
      }

      if (bstructDB.Astruct_AnotherarrayofbDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Astructs.get(bstructDB.Astruct_AnotherarrayofbDBID.Int64)?.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort;
    this.matTableDataSource.paginator = this.paginator;
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private bstructService: BstructService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of bstruct instances
    public dialogRef: MatDialogRef<BstructsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.bstructService.BstructServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getBstructs()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "Floatfield",
        "Intfield",
        "Astruct_Anarrayofb",
        "Astruct_Anotherarrayofb",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Floatfield",
        "Intfield",
        "Astruct_Anarrayofb",
        "Astruct_Anotherarrayofb",
      ]
      this.selection = new SelectionModel<BstructDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getBstructs()
    this.matTableDataSource = new MatTableDataSource(this.bstructs)
  }

  getBstructs(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.bstructs = this.frontRepo.Bstructs_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          this.bstructs.forEach(
            bstruct => {
              let ID = this.dialogData.ID
              let revPointer = bstruct[this.dialogData.ReversePointer]
              if (revPointer.Int64 == ID) {
                this.initialSelection.push(bstruct)
              }
            }
          )
          this.selection = new SelectionModel<BstructDB>(allowMultiSelect, this.initialSelection);
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s"]
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)

          if (sourceInstance[this.dialogData.SourceField]) {
            for (let associationInstance of sourceInstance[this.dialogData.SourceField]) {
              let bstruct = associationInstance[this.dialogData.IntermediateStructField]
              this.initialSelection.push(bstruct)
            }
          }
          this.selection = new SelectionModel<BstructDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.bstructs
      }
    )
  }

  // newBstruct initiate a new bstruct
  // create a new Bstruct objet
  newBstruct() {
  }

  deleteBstruct(bstructID: number, bstruct: BstructDB) {
    // list of bstructs is truncated of bstruct before the delete
    this.bstructs = this.bstructs.filter(h => h !== bstruct);

    this.bstructService.deleteBstruct(bstructID).subscribe(
      bstruct => {
        this.bstructService.BstructServiceChanged.next("delete")
      }
    );
  }

  editBstruct(bstructID: number, bstruct: BstructDB) {

  }

  // display bstruct in router
  displayBstructInRouter(bstructID: number) {
    this.router.navigate(["github_com_fullstack_lang_gong_test_go-" + "bstruct-display", bstructID])
  }

  // set editor outlet
  setEditorRouterOutlet(bstructID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gong_test_go_editor: ["github_com_fullstack_lang_gong_test_go-" + "bstruct-detail", bstructID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(bstructID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gong_test_go_presentation: ["github_com_fullstack_lang_gong_test_go-" + "bstruct-presentation", bstructID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.bstructs.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.bstructs.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<BstructDB>()

      // reset all initial selection of bstruct that belong to bstruct
      this.initialSelection.forEach(
        bstruct => {
          bstruct[this.dialogData.ReversePointer].Int64 = 0
          bstruct[this.dialogData.ReversePointer].Valid = true
          toUpdate.add(bstruct)
        }
      )

      // from selection, set bstruct that belong to bstruct
      this.selection.selected.forEach(
        bstruct => {
          let ID = +this.dialogData.ID
          bstruct[this.dialogData.ReversePointer].Int64 = ID
          bstruct[this.dialogData.ReversePointer].Valid = true
          toUpdate.add(bstruct)
        }
      )

      // update all bstruct (only update selection & initial selection)
      toUpdate.forEach(
        bstruct => {
          this.bstructService.updateBstruct(bstruct)
            .subscribe(bstruct => {
              this.bstructService.BstructServiceChanged.next("update")
            });
        }
      )
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s"]
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedBstruct = new Set<number>()
      for (let bstruct of this.initialSelection) {
        if (this.selection.selected.includes(bstruct)) {
          // console.log("bstruct " + bstruct.Name + " is still selected")
        } else {
          console.log("bstruct " + bstruct.Name + " has been unselected")
          unselectedBstruct.add(bstruct.ID)
          console.log("is unselected " + unselectedBstruct.has(bstruct.ID))
        }
      }

      // delete the association instance
      if (sourceInstance[this.dialogData.SourceField]) {
        for (let associationInstance of sourceInstance[this.dialogData.SourceField]) {
          let bstruct = associationInstance[this.dialogData.IntermediateStructField]
          if (unselectedBstruct.has(bstruct.ID)) {

            this.frontRepoService.deleteService( this.dialogData.IntermediateStruct, associationInstance )
          }
        }
      }

      // is the source array is emptyn create it
      if (sourceInstance[this.dialogData.SourceField] == undefined) {
        sourceInstance[this.dialogData.SourceField] = new Array<any>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField]) {
        this.selection.selected.forEach(
          bstruct => {
            if (!this.initialSelection.includes(bstruct)) {
              // console.log("bstruct " + bstruct.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + bstruct.Name,
              }

              associationInstance[this.dialogData.IntermediateStructField+"ID"] = new NullInt64
              associationInstance[this.dialogData.IntermediateStructField+"ID"].Int64 = bstruct.ID
              associationInstance[this.dialogData.IntermediateStructField+"ID"].Valid = true

              associationInstance[this.dialogData.SourceStruct + "_" + this.dialogData.SourceField + "DBID"] = new NullInt64
              associationInstance[this.dialogData.SourceStruct + "_" + this.dialogData.SourceField + "DBID"].Int64 = sourceInstance["ID"]
              associationInstance[this.dialogData.SourceStruct + "_" + this.dialogData.SourceField + "DBID"].Valid = true

              this.frontRepoService.postService( this.dialogData.IntermediateStruct, associationInstance )

            } else {
              // console.log("bstruct " + bstruct.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<BstructDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
