  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (bstructDB: BstructDB, property: string) => {
      switch (property) {
        case 'ID':
          return bstructDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return bstructDB.Name;

        case 'Floatfield':
          return bstructDB.Floatfield;

        case 'Floatfield2':
          return bstructDB.Floatfield2;

        case 'Intfield':
          return bstructDB.Intfield;

        case 'Astruct_Anarrayofb':
          if (this.frontRepo.Astructs.get(bstructDB.Astruct_AnarrayofbDBID.Int64) != undefined) {
            return this.frontRepo.Astructs.get(bstructDB.Astruct_AnarrayofbDBID.Int64)!.Name
          } else {
            return ""
          }

        case 'Astruct_Anotherarrayofb':
          if (this.frontRepo.Astructs.get(bstructDB.Astruct_AnotherarrayofbDBID.Int64) != undefined) {
            return this.frontRepo.Astructs.get(bstructDB.Astruct_AnotherarrayofbDBID.Int64)!.Name
          } else {
            return ""
          }

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (bstructDB: BstructDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the bstructDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += bstructDB.Name.toLowerCase()
      mergedContent += bstructDB.Floatfield.toString()
      mergedContent += bstructDB.Floatfield2.toString()
      mergedContent += bstructDB.Intfield.toString()
      if (bstructDB.Astruct_AnarrayofbDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Astructs.get(bstructDB.Astruct_AnarrayofbDBID.Int64)!.Name.toLowerCase()
      }

      if (bstructDB.Astruct_AnotherarrayofbDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Astructs.get(bstructDB.Astruct_AnotherarrayofbDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private bstructService: BstructService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of bstruct instances
    public dialogRef: MatDialogRef<BstructsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.bstructService.BstructServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getBstructs()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "Floatfield",
        "Floatfield2",
        "Intfield",
        "Astruct_Anarrayofb",
        "Astruct_Anotherarrayofb",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Floatfield",
        "Floatfield2",
        "Intfield",
        "Astruct_Anarrayofb",
        "Astruct_Anotherarrayofb",
      ]
      this.selection = new SelectionModel<BstructDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getBstructs()
    this.matTableDataSource = new MatTableDataSource(this.bstructs)
  }

  getBstructs(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.bstructs = this.frontRepo.Bstructs_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries
        
        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let bstruct of this.bstructs) {
            let ID = this.dialogData.ID
            let revPointer = bstruct[this.dialogData.ReversePointer as keyof BstructDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(bstruct)
            }
            this.selection = new SelectionModel<BstructDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, BstructDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as BstructDB[]
          for (let associationInstance of sourceField) {
            let bstruct = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as BstructDB
            this.initialSelection.push(bstruct)
          }

          this.selection = new SelectionModel<BstructDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.bstructs
      }
    )
  }

  // newBstruct initiate a new bstruct
