  displayBstructInRouter(bstructID: number) {
    this.router.navigate(["github_com_fullstack_lang_gong_test_go-" + "bstruct-display", bstructID])
  }

  // set editor outlet
  setEditorRouterOutlet(bstructID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gong_test_go_editor: ["github_com_fullstack_lang_gong_test_go-" + "bstruct-detail", bstructID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.bstructs.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.bstructs.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<BstructDB>()

      // reset all initial selection of bstruct that belong to bstruct
      for (let bstruct of this.initialSelection) {
        let index = bstruct[this.dialogData.ReversePointer as keyof BstructDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(bstruct)

      }

      // from selection, set bstruct that belong to bstruct
      for (let bstruct of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = bstruct[this.dialogData.ReversePointer as keyof BstructDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(bstruct)
      }


      // update all bstruct (only update selection & initial selection)
      for (let bstruct of toUpdate) {
        this.bstructService.updateBstruct(bstruct)
          .subscribe(bstruct => {
            this.bstructService.BstructServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, BstructDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedBstruct = new Set<number>()
      for (let bstruct of this.initialSelection) {
        if (this.selection.selected.includes(bstruct)) {
          // console.log("bstruct " + bstruct.Name + " is still selected")
        } else {
          console.log("bstruct " + bstruct.Name + " has been unselected")
          unselectedBstruct.add(bstruct.ID)
          console.log("is unselected " + unselectedBstruct.has(bstruct.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let bstruct = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as BstructDB
      if (unselectedBstruct.has(bstruct.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<BstructDB>) = new Array<BstructDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          bstruct => {
            if (!this.initialSelection.includes(bstruct)) {
              // console.log("bstruct " + bstruct.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + bstruct.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = bstruct.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = bstruct.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("bstruct " + bstruct.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<BstructDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
