import { Component, OnInit, Inject, Optional } from '@angular/core';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { DclassDB } from '../dclass-db'
import { DclassService } from '../dclass.service'

import { FrontRepoService, FrontRepo, NullInt64 } from '../front-repo.service'
@Component({
  selector: 'lib-dclass-sorting',
  templateUrl: './dclass-sorting.component.html',
  styleUrls: ['./dclass-sorting.component.css']
})
export class DclassSortingComponent implements OnInit {

  frontRepo: FrontRepo

  // array of Dclass instances that are in the association
  associatedDclasss = new Array<DclassDB>();

  constructor(
    private dclassService: DclassService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of dclass instances
    public dialogRef: MatDialogRef<DclassSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getDclasss()
  }

  getDclasss(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let dclass of this.frontRepo.Dclasss_array) {
          let ID = this.dialogData.ID
          let revPointerID = dclass[this.dialogData.ReversePointer]
          let revPointerID_Index = dclass[this.dialogData.ReversePointer+"_Index"]
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedDclasss.push(dclass)
          }
        }

        // sort associated dclass according to order
        this.associatedDclasss.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer+"_Index"]
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer+"_Index"]
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }  
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedDclasss, event.previousIndex, event.currentIndex);

    // set the order of Dclass instances
    let index = 0
    
    for (let dclass of this.associatedDclasss) {
      let revPointerID_Index = dclass[this.dialogData.ReversePointer+"_Index"]
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedDclasss.forEach(
      dclass => {
        this.dclassService.updateDclass(dclass)
          .subscribe(dclass => {
            this.dclassService.DclassServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of '+ this.dialogData.ReversePointer+' done');
  }
}
