// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { DclassDB } from '../dclass-db'
import { DclassService } from '../dclass.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'

// generated table component
@Component({
  selector: 'app-dclassstable',
  templateUrl: './dclasss-table.component.html',
  styleUrls: ['./dclasss-table.component.css'],
})
export class DclasssTableComponent implements OnInit {

  // used if the component is called as a selection component of Dclass instances
  selection: SelectionModel<DclassDB>;
  initialSelection = new Array<DclassDB>();

  // the data source for the table
  dclasss: DclassDB[];
  matTableDataSource: MatTableDataSource<DclassDB>


  // front repo, that will be referenced by this.dclasss
  frontRepo: FrontRepo

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort) sort: MatSort;
  @ViewChild(MatPaginator) paginator: MatPaginator;

  ngAfterViewInit() {

	// enable sorting on all fields (including pointers and reverse pointer)
	this.matTableDataSource.sortingDataAccessor = (dclassDB: DclassDB, property: string) => {
		switch (property) {
				// insertion point for specific sorting accessor
				default:
					return DclassDB[property];
		}
	}; 

	// enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
	this.matTableDataSource.filterPredicate = (dclassDB: DclassDB, filter: string) => {

		// filtering is based on finding a lower case filter into a concatenated string
		// the dclassDB properties
		let mergedContent = ""

		// insertion point for merging of fields
		mergedContent += dclassDB.Name.toLowerCase()

		let isSelected = mergedContent.includes(filter.toLowerCase())
		return isSelected
	};

    this.matTableDataSource.sort = this.sort;
    this.matTableDataSource.paginator = this.paginator;
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private dclassService: DclassService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of dclass instances
    public dialogRef: MatDialogRef<DclasssTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    // observable for changes in structs
    this.dclassService.DclassServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getDclasss()
        }
      }
    )
    if (dialogData == undefined) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
      ]
      this.selection = new SelectionModel<DclassDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getDclasss()
    this.matTableDataSource = new MatTableDataSource(this.dclasss)
  }

  getDclasss(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.dclasss = this.frontRepo.Dclasss_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.dialogData != undefined) {
          this.dclasss.forEach(
            dclass => {
              let ID = this.dialogData.ID
              let revPointer = dclass[this.dialogData.ReversePointer]
              if (revPointer.Int64 == ID) {
                this.initialSelection.push(dclass)
              }
            }
          )
          this.selection = new SelectionModel<DclassDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.dclasss
      }
    )
  }

  // newDclass initiate a new dclass
  // create a new Dclass objet
  newDclass() {
  }

  deleteDclass(dclassID: number, dclass: DclassDB) {
    // list of dclasss is truncated of dclass before the delete
    this.dclasss = this.dclasss.filter(h => h !== dclass);

    this.dclassService.deleteDclass(dclassID).subscribe(
      dclass => {
        this.dclassService.DclassServiceChanged.next("delete")
      }
    );
  }

  editDclass(dclassID: number, dclass: DclassDB) {

  }

  // display dclass in router
  displayDclassInRouter(dclassID: number) {
    this.router.navigate(["githubcomfullstacklanggongtestgo-" + "dclass-display", dclassID])
  }

  // set editor outlet
  setEditorRouterOutlet(dclassID: number) {
    this.router.navigate([{
      outlets: {
        githubcomfullstacklanggongtestgoeditor: ["githubcomfullstacklanggongtestgo-" + "dclass-detail", dclassID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(dclassID: number) {
    this.router.navigate([{
      outlets: {
        githubcomfullstacklanggongtestgopresentation: ["githubcomfullstacklanggongtestgo-" + "dclass-presentation", dclassID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.dclasss.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.dclasss.forEach(row => this.selection.select(row));
  }

  save() {

    let toUpdate = new Set<DclassDB>()

    // reset all initial selection of dclass that belong to dclass through Anarrayofb
    this.initialSelection.forEach(
      dclass => {
        dclass[this.dialogData.ReversePointer].Int64 = 0
        dclass[this.dialogData.ReversePointer].Valid = true
        toUpdate.add(dclass)
      }
    )

    // from selection, set dclass that belong to dclass through Anarrayofb
    this.selection.selected.forEach(
      dclass => {
        let ID = +this.dialogData.ID
        dclass[this.dialogData.ReversePointer].Int64 = ID
        dclass[this.dialogData.ReversePointer].Valid = true
        toUpdate.add(dclass)
      }
    )

    // update all dclass (only update selection & initial selection)
    toUpdate.forEach(
      dclass => {
        this.dclassService.updateDclass(dclass)
          .subscribe(dclass => {
            this.dclassService.DclassServiceChanged.next("update")
          });
      }
    )
    this.dialogRef.close('Pizza!');
  }
}
