// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-dstructstable',
  templateUrl: './dstructs-table.component.html',
  styleUrls: ['./dstructs-table.component.css'],
})
export class DstructsTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of Dstruct instances
  selection: SelectionModel<DstructDB> = new (SelectionModel)
  initialSelection = new Array<DstructDB>()

  // the data source for the table
  dstructs: DstructDB[] = []
  matTableDataSource: MatTableDataSource<DstructDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.dstructs
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (dstructDB: DstructDB, property: string) => {
      switch (property) {
        case 'ID':
          return dstructDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return dstructDB.Name;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (dstructDB: DstructDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the dstructDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += dstructDB.Name.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private dstructService: DstructService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of dstruct instances
    public dialogRef: MatDialogRef<DstructsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.dstructService.DstructServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getDstructs()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
      ]
      this.selection = new SelectionModel<DstructDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getDstructs()

    this.matTableDataSource = new MatTableDataSource(this.dstructs)
  }

  getDstructs(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.dstructs = this.frontRepo.Dstructs_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let dstruct of this.dstructs) {
            let ID = this.dialogData.ID
            let revPointer = dstruct[this.dialogData.ReversePointer as keyof DstructDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(dstruct)
            }
            this.selection = new SelectionModel<DstructDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, DstructDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as DstructDB[]
          for (let associationInstance of sourceField) {
            let dstruct = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as DstructDB
            this.initialSelection.push(dstruct)
          }

          this.selection = new SelectionModel<DstructDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.dstructs
      }
    )
  }

  // newDstruct initiate a new dstruct
  // create a new Dstruct objet
  newDstruct() {
  }

  deleteDstruct(dstructID: number, dstruct: DstructDB) {
    // list of dstructs is truncated of dstruct before the delete
    this.dstructs = this.dstructs.filter(h => h !== dstruct);

    this.dstructService.deleteDstruct(dstructID, this.dialogData.GONG__StackPath).subscribe(
      dstruct => {
        this.dstructService.DstructServiceChanged.next("delete")
      }
    );
  }

  editDstruct(dstructID: number, dstruct: DstructDB) {

  }

  // display dstruct in router
  displayDstructInRouter(dstructID: number) {
    this.router.navigate(["github_com_fullstack_lang_gong_test_go-" + "dstruct-display", dstructID])
  }

  // set editor outlet
  setEditorRouterOutlet(dstructID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gong_test_go_editor: ["github_com_fullstack_lang_gong_test_go-" + "dstruct-detail", dstructID, this.GONG__StackPath]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.dstructs.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.dstructs.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<DstructDB>()

      // reset all initial selection of dstruct that belong to dstruct
      for (let dstruct of this.initialSelection) {
        let index = dstruct[this.dialogData.ReversePointer as keyof DstructDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(dstruct)

      }

      // from selection, set dstruct that belong to dstruct
      for (let dstruct of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = dstruct[this.dialogData.ReversePointer as keyof DstructDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(dstruct)
      }


      // update all dstruct (only update selection & initial selection)
      for (let dstruct of toUpdate) {
        this.dstructService.updateDstruct(dstruct, this.GONG__StackPath)
          .subscribe(dstruct => {
            this.dstructService.DstructServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, DstructDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedDstruct = new Set<number>()
      for (let dstruct of this.initialSelection) {
        if (this.selection.selected.includes(dstruct)) {
          // console.log("dstruct " + dstruct.Name + " is still selected")
        } else {
          console.log("dstruct " + dstruct.Name + " has been unselected")
          unselectedDstruct.add(dstruct.ID)
          console.log("is unselected " + unselectedDstruct.has(dstruct.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let dstruct = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as DstructDB
      if (unselectedDstruct.has(dstruct.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<DstructDB>) = new Array<DstructDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          dstruct => {
            if (!this.initialSelection.includes(dstruct)) {
              // console.log("dstruct " + dstruct.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + dstruct.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = dstruct.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = dstruct.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("dstruct " + dstruct.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<DstructDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
