	filename := filepath.Join(dirPath, "ArchitectureDB.json")
	jsonFile, err := os.Open(filename)
	// if we os.Open returns an error then handle it
	if err != nil {
		log.Fatal("Cannot restore/open the json Architecture file", filename, " ", err.Error())
	}

	// read our opened jsonFile as a byte array.
	byteValue, _ := ioutil.ReadAll(jsonFile)

	var forRestore []*ArchitectureDB

	err = json.Unmarshal(byteValue, &forRestore)

	// fill up Map_ArchitectureDBID_ArchitectureDB
	for _, architectureDB := range forRestore {

		architectureDB_ID_atBackupTime := architectureDB.ID
		architectureDB.ID = 0
		_, err := backRepoArchitecture.db.Create(architectureDB)
		if err != nil {
			log.Fatal(err)
		}
		backRepoArchitecture.Map_ArchitectureDBID_ArchitectureDB[architectureDB.ID] = architectureDB
		BackRepoArchitectureid_atBckpTime_newID[architectureDB_ID_atBackupTime] = architectureDB.ID
	}

	if err != nil {
		log.Fatal("Cannot restore/unmarshall json Architecture file", err.Error())
	}
