	row.WriteSlice(&Diagram_Fields, -1)
	for _, diagramDB := range forBackup {

		var diagramWOP DiagramWOP
		diagramDB.CopyBasicFieldsToDiagramWOP(&diagramWOP)

		row := sh.AddRow()
		row.WriteStruct(&diagramWOP, -1)
	}
