import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { ClassdiagramDB } from '../classdiagram-db'
import { ClassdiagramService } from '../classdiagram.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'
import { NullInt64 } from '../null-int64'

@Component({
  selector: 'lib-classdiagram-sorting',
  templateUrl: './classdiagram-sorting.component.html',
  styleUrls: ['./classdiagram-sorting.component.css']
})
export class ClassdiagramSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of Classdiagram instances that are in the association
  associatedClassdiagrams = new Array<ClassdiagramDB>();

  constructor(
    private classdiagramService: ClassdiagramService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of classdiagram instances
    public dialogRef: MatDialogRef<ClassdiagramSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getClassdiagrams()
  }

  getClassdiagrams(): void {
    this.frontRepoService.pull(this.dialogData.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let classdiagram of this.frontRepo.Classdiagrams_array) {
          let ID = this.dialogData.ID
          let revPointerID = classdiagram[this.dialogData.ReversePointer as keyof ClassdiagramDB] as unknown as NullInt64
          let revPointerID_Index = classdiagram[this.dialogData.ReversePointer + "_Index" as keyof ClassdiagramDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedClassdiagrams.push(classdiagram)
          }
        }

        // sort associated classdiagram according to order
        this.associatedClassdiagrams.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedClassdiagrams, event.previousIndex, event.currentIndex);

    // set the order of Classdiagram instances
    let index = 0

    for (let classdiagram of this.associatedClassdiagrams) {
      let revPointerID_Index = classdiagram[this.dialogData.ReversePointer + "_Index" as keyof ClassdiagramDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedClassdiagrams.forEach(
      classdiagram => {
        this.classdiagramService.updateClassdiagram(classdiagram, this.dialogData.GONG__StackPath)
          .subscribe(classdiagram => {
            this.classdiagramService.ClassdiagramServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer + ' done');
  }
}
