// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { ClassdiagramDB } from '../classdiagram-db'
import { ClassdiagramService } from '../classdiagram.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-classdiagramstable',
  templateUrl: './classdiagrams-table.component.html',
  styleUrls: ['./classdiagrams-table.component.css'],
})
export class ClassdiagramsTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of Classdiagram instances
  selection: SelectionModel<ClassdiagramDB> = new (SelectionModel)
  initialSelection = new Array<ClassdiagramDB>()

  // the data source for the table
  classdiagrams: ClassdiagramDB[] = []
  matTableDataSource: MatTableDataSource<ClassdiagramDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.classdiagrams
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (classdiagramDB: ClassdiagramDB, property: string) => {
      switch (property) {
        case 'ID':
          return classdiagramDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return classdiagramDB.Name;

        case 'IsInDrawMode':
          return classdiagramDB.IsInDrawMode ? "true" : "false";

        case 'DiagramPackage_Classdiagrams':
          if (this.frontRepo.DiagramPackages.get(classdiagramDB.DiagramPackage_ClassdiagramsDBID.Int64) != undefined) {
            return this.frontRepo.DiagramPackages.get(classdiagramDB.DiagramPackage_ClassdiagramsDBID.Int64)!.Name
          } else {
            return ""
          }

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (classdiagramDB: ClassdiagramDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the classdiagramDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += classdiagramDB.Name.toLowerCase()
      if (classdiagramDB.DiagramPackage_ClassdiagramsDBID.Int64 != 0) {
        mergedContent += this.frontRepo.DiagramPackages.get(classdiagramDB.DiagramPackage_ClassdiagramsDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private classdiagramService: ClassdiagramService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of classdiagram instances
    public dialogRef: MatDialogRef<ClassdiagramsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.classdiagramService.ClassdiagramServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getClassdiagrams()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "IsInDrawMode",
        "DiagramPackage_Classdiagrams",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "IsInDrawMode",
        "DiagramPackage_Classdiagrams",
      ]
      this.selection = new SelectionModel<ClassdiagramDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getClassdiagrams()

    this.matTableDataSource = new MatTableDataSource(this.classdiagrams)
  }

  getClassdiagrams(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.classdiagrams = this.frontRepo.Classdiagrams_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let classdiagram of this.classdiagrams) {
            let ID = this.dialogData.ID
            let revPointer = classdiagram[this.dialogData.ReversePointer as keyof ClassdiagramDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(classdiagram)
            }
            this.selection = new SelectionModel<ClassdiagramDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, ClassdiagramDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to ClassdiagramDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as ClassdiagramDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let classdiagram = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as ClassdiagramDB
              this.initialSelection.push(classdiagram)
            }
          }

          this.selection = new SelectionModel<ClassdiagramDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.classdiagrams
      }
    )
  }

  // newClassdiagram initiate a new classdiagram
  // create a new Classdiagram objet
  newClassdiagram() {
  }

  deleteClassdiagram(classdiagramID: number, classdiagram: ClassdiagramDB) {
    // list of classdiagrams is truncated of classdiagram before the delete
    this.classdiagrams = this.classdiagrams.filter(h => h !== classdiagram);

    this.classdiagramService.deleteClassdiagram(classdiagramID, this.GONG__StackPath).subscribe(
      classdiagram => {
        this.classdiagramService.ClassdiagramServiceChanged.next("delete")
      }
    );
  }

  editClassdiagram(classdiagramID: number, classdiagram: ClassdiagramDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(classdiagramID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "classdiagram" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, classdiagramID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.classdiagrams.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.classdiagrams.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<ClassdiagramDB>()

      // reset all initial selection of classdiagram that belong to classdiagram
      for (let classdiagram of this.initialSelection) {
        let index = classdiagram[this.dialogData.ReversePointer as keyof ClassdiagramDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(classdiagram)

      }

      // from selection, set classdiagram that belong to classdiagram
      for (let classdiagram of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = classdiagram[this.dialogData.ReversePointer as keyof ClassdiagramDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(classdiagram)
      }


      // update all classdiagram (only update selection & initial selection)
      for (let classdiagram of toUpdate) {
        this.classdiagramService.updateClassdiagram(classdiagram, this.GONG__StackPath)
          .subscribe(classdiagram => {
            this.classdiagramService.ClassdiagramServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, ClassdiagramDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedClassdiagram = new Set<number>()
      for (let classdiagram of this.initialSelection) {
        if (this.selection.selected.includes(classdiagram)) {
          // console.log("classdiagram " + classdiagram.Name + " is still selected")
        } else {
          console.log("classdiagram " + classdiagram.Name + " has been unselected")
          unselectedClassdiagram.add(classdiagram.ID)
          console.log("is unselected " + unselectedClassdiagram.has(classdiagram.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let classdiagram = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as ClassdiagramDB
      if (unselectedClassdiagram.has(classdiagram.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<ClassdiagramDB>) = new Array<ClassdiagramDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          classdiagram => {
            if (!this.initialSelection.includes(classdiagram)) {
              // console.log("classdiagram " + classdiagram.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + classdiagram.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = classdiagram.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = classdiagram.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("classdiagram " + classdiagram.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<ClassdiagramDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
