export class DiagramPackageSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of DiagramPackage instances that are in the association
  associatedDiagramPackages = new Array<DiagramPackageDB>();

  constructor(
    private diagrampackageService: DiagramPackageService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of diagrampackage instances
    public dialogRef: MatDialogRef<DiagramPackageSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getDiagramPackages()
  }

  getDiagramPackages(): void {
    this.frontRepoService.pull(this.dialogData.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let diagrampackage of this.frontRepo.DiagramPackages_array) {
          let ID = this.dialogData.ID
          let revPointerID = diagrampackage[this.dialogData.ReversePointer as keyof DiagramPackageDB] as unknown as NullInt64
          let revPointerID_Index = diagrampackage[this.dialogData.ReversePointer + "_Index" as keyof DiagramPackageDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedDiagramPackages.push(diagrampackage)
          }
        }

        // sort associated diagrampackage according to order
        this.associatedDiagramPackages.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedDiagramPackages, event.previousIndex, event.currentIndex);

    // set the order of DiagramPackage instances
    let index = 0

    for (let diagrampackage of this.associatedDiagramPackages) {
      let revPointerID_Index = diagrampackage[this.dialogData.ReversePointer + "_Index" as keyof DiagramPackageDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedDiagramPackages.forEach(
      diagrampackage => {
        this.diagrampackageService.updateDiagramPackage(diagrampackage, this.dialogData.GONG__StackPath)
          .subscribe(diagrampackage => {
            this.diagrampackageService.DiagramPackageServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer + ' done');
  }
}
