// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { GongEnumShapeDB } from '../gongenumshape-db'
import { GongEnumShapeService } from '../gongenumshape.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-gongenumshapestable',
  templateUrl: './gongenumshapes-table.component.html',
  styleUrls: ['./gongenumshapes-table.component.css'],
})
export class GongEnumShapesTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of GongEnumShape instances
  selection: SelectionModel<GongEnumShapeDB> = new (SelectionModel)
  initialSelection = new Array<GongEnumShapeDB>()

  // the data source for the table
  gongenumshapes: GongEnumShapeDB[] = []
  matTableDataSource: MatTableDataSource<GongEnumShapeDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.gongenumshapes
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (gongenumshapeDB: GongEnumShapeDB, property: string) => {
      switch (property) {
        case 'ID':
          return gongenumshapeDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return gongenumshapeDB.Name;

        case 'Position':
          return (gongenumshapeDB.Position ? gongenumshapeDB.Position.Name : '');

        case 'Identifier':
          return gongenumshapeDB.Identifier;

        case 'Width':
          return gongenumshapeDB.Width;

        case 'Heigth':
          return gongenumshapeDB.Heigth;

        case 'Classdiagram_GongEnumShapes':
          if (this.frontRepo.Classdiagrams.get(gongenumshapeDB.Classdiagram_GongEnumShapesDBID.Int64) != undefined) {
            return this.frontRepo.Classdiagrams.get(gongenumshapeDB.Classdiagram_GongEnumShapesDBID.Int64)!.Name
          } else {
            return ""
          }

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (gongenumshapeDB: GongEnumShapeDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the gongenumshapeDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += gongenumshapeDB.Name.toLowerCase()
      if (gongenumshapeDB.Position) {
        mergedContent += gongenumshapeDB.Position.Name.toLowerCase()
      }
      mergedContent += gongenumshapeDB.Identifier.toLowerCase()
      mergedContent += gongenumshapeDB.Width.toString()
      mergedContent += gongenumshapeDB.Heigth.toString()
      if (gongenumshapeDB.Classdiagram_GongEnumShapesDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Classdiagrams.get(gongenumshapeDB.Classdiagram_GongEnumShapesDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private gongenumshapeService: GongEnumShapeService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of gongenumshape instances
    public dialogRef: MatDialogRef<GongEnumShapesTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.gongenumshapeService.GongEnumShapeServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getGongEnumShapes()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Position",
        "Identifier",
        "Width",
        "Heigth",
        "Classdiagram_GongEnumShapes",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Position",
        "Identifier",
        "Width",
        "Heigth",
        "Classdiagram_GongEnumShapes",
      ]
      this.selection = new SelectionModel<GongEnumShapeDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getGongEnumShapes()

    this.matTableDataSource = new MatTableDataSource(this.gongenumshapes)
  }

  getGongEnumShapes(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.gongenumshapes = this.frontRepo.GongEnumShapes_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let gongenumshape of this.gongenumshapes) {
            let ID = this.dialogData.ID
            let revPointer = gongenumshape[this.dialogData.ReversePointer as keyof GongEnumShapeDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(gongenumshape)
            }
            this.selection = new SelectionModel<GongEnumShapeDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, GongEnumShapeDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to GongEnumShapeDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as GongEnumShapeDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let gongenumshape = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as GongEnumShapeDB
              this.initialSelection.push(gongenumshape)
            }
          }

          this.selection = new SelectionModel<GongEnumShapeDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.gongenumshapes
      }
    )
  }

  // newGongEnumShape initiate a new gongenumshape
  // create a new GongEnumShape objet
  newGongEnumShape() {
  }

  deleteGongEnumShape(gongenumshapeID: number, gongenumshape: GongEnumShapeDB) {
    // list of gongenumshapes is truncated of gongenumshape before the delete
    this.gongenumshapes = this.gongenumshapes.filter(h => h !== gongenumshape);

    this.gongenumshapeService.deleteGongEnumShape(gongenumshapeID, this.GONG__StackPath).subscribe(
      gongenumshape => {
        this.gongenumshapeService.GongEnumShapeServiceChanged.next("delete")
      }
    );
  }

  editGongEnumShape(gongenumshapeID: number, gongenumshape: GongEnumShapeDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(gongenumshapeID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "gongenumshape" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, gongenumshapeID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.gongenumshapes.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.gongenumshapes.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<GongEnumShapeDB>()

      // reset all initial selection of gongenumshape that belong to gongenumshape
      for (let gongenumshape of this.initialSelection) {
        let index = gongenumshape[this.dialogData.ReversePointer as keyof GongEnumShapeDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(gongenumshape)

      }

      // from selection, set gongenumshape that belong to gongenumshape
      for (let gongenumshape of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = gongenumshape[this.dialogData.ReversePointer as keyof GongEnumShapeDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(gongenumshape)
      }


      // update all gongenumshape (only update selection & initial selection)
      for (let gongenumshape of toUpdate) {
        this.gongenumshapeService.updateGongEnumShape(gongenumshape, this.GONG__StackPath)
          .subscribe(gongenumshape => {
            this.gongenumshapeService.GongEnumShapeServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, GongEnumShapeDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedGongEnumShape = new Set<number>()
      for (let gongenumshape of this.initialSelection) {
        if (this.selection.selected.includes(gongenumshape)) {
          // console.log("gongenumshape " + gongenumshape.Name + " is still selected")
        } else {
          console.log("gongenumshape " + gongenumshape.Name + " has been unselected")
          unselectedGongEnumShape.add(gongenumshape.ID)
          console.log("is unselected " + unselectedGongEnumShape.has(gongenumshape.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let gongenumshape = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as GongEnumShapeDB
      if (unselectedGongEnumShape.has(gongenumshape.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<GongEnumShapeDB>) = new Array<GongEnumShapeDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          gongenumshape => {
            if (!this.initialSelection.includes(gongenumshape)) {
              // console.log("gongenumshape " + gongenumshape.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + gongenumshape.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = gongenumshape.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = gongenumshape.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("gongenumshape " + gongenumshape.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<GongEnumShapeDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
