  ngOnInit(): void {
    this.getLinks()
  }

  getLinks(): void {
    this.frontRepoService.pull(this.dialogData.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let link of this.frontRepo.Links_array) {
          let ID = this.dialogData.ID
          let revPointerID = link[this.dialogData.ReversePointer as keyof LinkDB] as unknown as NullInt64
          let revPointerID_Index = link[this.dialogData.ReversePointer + "_Index" as keyof LinkDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedLinks.push(link)
          }
        }

        // sort associated link according to order
        this.associatedLinks.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedLinks, event.previousIndex, event.currentIndex);

    // set the order of Link instances
    let index = 0

    for (let link of this.associatedLinks) {
      let revPointerID_Index = link[this.dialogData.ReversePointer + "_Index" as keyof LinkDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedLinks.forEach(
      link => {
        this.linkService.updateLink(link, this.dialogData.GONG__StackPath)
          .subscribe(link => {
            this.linkService.LinkServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer + ' done');
  }
