  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.notelinks.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.notelinks.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<NoteLinkDB>()

      // reset all initial selection of notelink that belong to notelink
      for (let notelink of this.initialSelection) {
        let index = notelink[this.dialogData.ReversePointer as keyof NoteLinkDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(notelink)

      }

      // from selection, set notelink that belong to notelink
      for (let notelink of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = notelink[this.dialogData.ReversePointer as keyof NoteLinkDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(notelink)
      }


      // update all notelink (only update selection & initial selection)
      for (let notelink of toUpdate) {
        this.notelinkService.updateNoteLink(notelink)
          .subscribe(notelink => {
            this.notelinkService.NoteLinkServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, NoteLinkDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedNoteLink = new Set<number>()
      for (let notelink of this.initialSelection) {
        if (this.selection.selected.includes(notelink)) {
          // console.log("notelink " + notelink.Name + " is still selected")
        } else {
          console.log("notelink " + notelink.Name + " has been unselected")
          unselectedNoteLink.add(notelink.ID)
          console.log("is unselected " + unselectedNoteLink.has(notelink.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let notelink = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as NoteLinkDB
      if (unselectedNoteLink.has(notelink.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<NoteLinkDB>) = new Array<NoteLinkDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          notelink => {
            if (!this.initialSelection.includes(notelink)) {
              // console.log("notelink " + notelink.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + notelink.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = notelink.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = notelink.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("notelink " + notelink.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<NoteLinkDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
