import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { NoteShapeLinkDB } from '../noteshapelink-db'
import { NoteShapeLinkService } from '../noteshapelink.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-noteshapelinkstable',
  templateUrl: './noteshapelinks-table.component.html',
  styleUrls: ['./noteshapelinks-table.component.css'],
})
export class NoteShapeLinksTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of NoteShapeLink instances
  selection: SelectionModel<NoteShapeLinkDB> = new (SelectionModel)
  initialSelection = new Array<NoteShapeLinkDB>()

  // the data source for the table
  noteshapelinks: NoteShapeLinkDB[] = []
  matTableDataSource: MatTableDataSource<NoteShapeLinkDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.noteshapelinks
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (noteshapelinkDB: NoteShapeLinkDB, property: string) => {
      switch (property) {
        case 'ID':
          return noteshapelinkDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return noteshapelinkDB.Name;

        case 'Identifier':
          return noteshapelinkDB.Identifier;

        case 'Type':
          return noteshapelinkDB.Type;

        case 'NoteShape_NoteShapeLinks':
          if (this.frontRepo.NoteShapes.get(noteshapelinkDB.NoteShape_NoteShapeLinksDBID.Int64) != undefined) {
            return this.frontRepo.NoteShapes.get(noteshapelinkDB.NoteShape_NoteShapeLinksDBID.Int64)!.Name
          } else {
            return ""
          }

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (noteshapelinkDB: NoteShapeLinkDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the noteshapelinkDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += noteshapelinkDB.Name.toLowerCase()
      mergedContent += noteshapelinkDB.Identifier.toLowerCase()
      mergedContent += noteshapelinkDB.Type.toLowerCase()
      if (noteshapelinkDB.NoteShape_NoteShapeLinksDBID.Int64 != 0) {
        mergedContent += this.frontRepo.NoteShapes.get(noteshapelinkDB.NoteShape_NoteShapeLinksDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private noteshapelinkService: NoteShapeLinkService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of noteshapelink instances
    public dialogRef: MatDialogRef<NoteShapeLinksTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.noteshapelinkService.NoteShapeLinkServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getNoteShapeLinks()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Identifier",
        "Type",
        "NoteShape_NoteShapeLinks",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Identifier",
        "Type",
        "NoteShape_NoteShapeLinks",
      ]
      this.selection = new SelectionModel<NoteShapeLinkDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getNoteShapeLinks()

    this.matTableDataSource = new MatTableDataSource(this.noteshapelinks)
  }

  getNoteShapeLinks(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.noteshapelinks = this.frontRepo.NoteShapeLinks_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let noteshapelink of this.noteshapelinks) {
            let ID = this.dialogData.ID
            let revPointer = noteshapelink[this.dialogData.ReversePointer as keyof NoteShapeLinkDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(noteshapelink)
            }
            this.selection = new SelectionModel<NoteShapeLinkDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, NoteShapeLinkDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to NoteShapeLinkDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as NoteShapeLinkDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let noteshapelink = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as NoteShapeLinkDB
              this.initialSelection.push(noteshapelink)
            }
          }

          this.selection = new SelectionModel<NoteShapeLinkDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.noteshapelinks
      }
    )
  }

  // newNoteShapeLink initiate a new noteshapelink
  // create a new NoteShapeLink objet
  newNoteShapeLink() {
  }

  deleteNoteShapeLink(noteshapelinkID: number, noteshapelink: NoteShapeLinkDB) {
    // list of noteshapelinks is truncated of noteshapelink before the delete
    this.noteshapelinks = this.noteshapelinks.filter(h => h !== noteshapelink);

    this.noteshapelinkService.deleteNoteShapeLink(noteshapelinkID, this.GONG__StackPath).subscribe(
      noteshapelink => {
        this.noteshapelinkService.NoteShapeLinkServiceChanged.next("delete")
      }
    );
  }

  editNoteShapeLink(noteshapelinkID: number, noteshapelink: NoteShapeLinkDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(noteshapelinkID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "noteshapelink" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, noteshapelinkID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.noteshapelinks.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.noteshapelinks.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<NoteShapeLinkDB>()

      // reset all initial selection of noteshapelink that belong to noteshapelink
      for (let noteshapelink of this.initialSelection) {
        let index = noteshapelink[this.dialogData.ReversePointer as keyof NoteShapeLinkDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(noteshapelink)

      }

      // from selection, set noteshapelink that belong to noteshapelink
      for (let noteshapelink of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = noteshapelink[this.dialogData.ReversePointer as keyof NoteShapeLinkDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(noteshapelink)
      }


      // update all noteshapelink (only update selection & initial selection)
      for (let noteshapelink of toUpdate) {
        this.noteshapelinkService.updateNoteShapeLink(noteshapelink, this.GONG__StackPath)
          .subscribe(noteshapelink => {
            this.noteshapelinkService.NoteShapeLinkServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, NoteShapeLinkDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedNoteShapeLink = new Set<number>()
      for (let noteshapelink of this.initialSelection) {
        if (this.selection.selected.includes(noteshapelink)) {
          // console.log("noteshapelink " + noteshapelink.Name + " is still selected")
        } else {
          console.log("noteshapelink " + noteshapelink.Name + " has been unselected")
          unselectedNoteShapeLink.add(noteshapelink.ID)
          console.log("is unselected " + unselectedNoteShapeLink.has(noteshapelink.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let noteshapelink = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as NoteShapeLinkDB
      if (unselectedNoteShapeLink.has(noteshapelink.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<NoteShapeLinkDB>) = new Array<NoteShapeLinkDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          noteshapelink => {
            if (!this.initialSelection.includes(noteshapelink)) {
              // console.log("noteshapelink " + noteshapelink.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + noteshapelink.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = noteshapelink.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = noteshapelink.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("noteshapelink " + noteshapelink.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<NoteShapeLinkDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
