// insertion point for additional imports

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-referencestable',
  templateUrl: './references-table.component.html',
  styleUrls: ['./references-table.component.css'],
})
export class ReferencesTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of Reference instances
  selection: SelectionModel<ReferenceDB> = new (SelectionModel)
  initialSelection = new Array<ReferenceDB>()

  // the data source for the table
  references: ReferenceDB[] = []
  matTableDataSource: MatTableDataSource<ReferenceDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.references
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (referenceDB: ReferenceDB, property: string) => {
      switch (property) {
        case 'ID':
          return referenceDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return referenceDB.Name;

        case 'NbInstances':
          return referenceDB.NbInstances;

        case 'Type':
          return referenceDB.Type;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (referenceDB: ReferenceDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the referenceDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += referenceDB.Name.toLowerCase()
      mergedContent += referenceDB.NbInstances.toString()
      mergedContent += referenceDB.Type.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private referenceService: ReferenceService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of reference instances
    public dialogRef: MatDialogRef<ReferencesTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.referenceService.ReferenceServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getReferences()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "NbInstances",
        "Type",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "NbInstances",
        "Type",
      ]
      this.selection = new SelectionModel<ReferenceDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getReferences()
    this.matTableDataSource = new MatTableDataSource(this.references)
  }

  getReferences(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.references = this.frontRepo.References_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries
        
        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let reference of this.references) {
            let ID = this.dialogData.ID
            let revPointer = reference[this.dialogData.ReversePointer as keyof ReferenceDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(reference)
            }
            this.selection = new SelectionModel<ReferenceDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, ReferenceDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as ReferenceDB[]
          for (let associationInstance of sourceField) {
            let reference = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as ReferenceDB
            this.initialSelection.push(reference)
          }

          this.selection = new SelectionModel<ReferenceDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.references
      }
    )
  }

  // newReference initiate a new reference
  // create a new Reference objet
  newReference() {
  }

  deleteReference(referenceID: number, reference: ReferenceDB) {
    // list of references is truncated of reference before the delete
    this.references = this.references.filter(h => h !== reference);

    this.referenceService.deleteReference(referenceID).subscribe(
      reference => {
        this.referenceService.ReferenceServiceChanged.next("delete")
      }
    );
  }

  editReference(referenceID: number, reference: ReferenceDB) {

  }

  // display reference in router
  displayReferenceInRouter(referenceID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongdoc_go-" + "reference-display", referenceID])
  }

  // set editor outlet
  setEditorRouterOutlet(referenceID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongdoc_go_editor: ["github_com_fullstack_lang_gongdoc_go-" + "reference-detail", referenceID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(referenceID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongdoc_go_presentation: ["github_com_fullstack_lang_gongdoc_go-" + "reference-presentation", referenceID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.references.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.references.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<ReferenceDB>()

      // reset all initial selection of reference that belong to reference
      for (let reference of this.initialSelection) {
        let index = reference[this.dialogData.ReversePointer as keyof ReferenceDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(reference)

      }

      // from selection, set reference that belong to reference
      for (let reference of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = reference[this.dialogData.ReversePointer as keyof ReferenceDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(reference)
      }


      // update all reference (only update selection & initial selection)
      for (let reference of toUpdate) {
        this.referenceService.updateReference(reference)
          .subscribe(reference => {
            this.referenceService.ReferenceServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, ReferenceDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedReference = new Set<number>()
      for (let reference of this.initialSelection) {
        if (this.selection.selected.includes(reference)) {
          // console.log("reference " + reference.Name + " is still selected")
        } else {
          console.log("reference " + reference.Name + " has been unselected")
          unselectedReference.add(reference.ID)
          console.log("is unselected " + unselectedReference.has(reference.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let reference = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as ReferenceDB
      if (unselectedReference.has(reference.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<ReferenceDB>) = new Array<ReferenceDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          reference => {
            if (!this.initialSelection.includes(reference)) {
              // console.log("reference " + reference.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + reference.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = reference.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = reference.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("reference " + reference.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<ReferenceDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
