export class UmlscSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of Umlsc instances that are in the association
  associatedUmlscs = new Array<UmlscDB>();

  constructor(
    private umlscService: UmlscService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of umlsc instances
    public dialogRef: MatDialogRef<UmlscSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getUmlscs()
  }

  getUmlscs(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let umlsc of this.frontRepo.Umlscs_array) {
          let ID = this.dialogData.ID
          let revPointerID = umlsc[this.dialogData.ReversePointer as keyof UmlscDB] as unknown as NullInt64
          let revPointerID_Index = umlsc[this.dialogData.ReversePointer + "_Index" as keyof UmlscDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedUmlscs.push(umlsc)
          }
        }

        // sort associated umlsc according to order
        this.associatedUmlscs.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedUmlscs, event.previousIndex, event.currentIndex);

    // set the order of Umlsc instances
    let index = 0

    for (let umlsc of this.associatedUmlscs) {
      let revPointerID_Index = umlsc[this.dialogData.ReversePointer + "_Index" as keyof UmlscDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedUmlscs.forEach(
      umlsc => {
        this.umlscService.updateUmlsc(umlsc)
          .subscribe(umlsc => {
            this.umlscService.UmlscServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer +' done');
  }
}
