// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { EllipseDB } from '../ellipse-db'
import { EllipseService } from '../ellipse.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-ellipsestable',
  templateUrl: './ellipses-table.component.html',
  styleUrls: ['./ellipses-table.component.css'],
})
export class EllipsesTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of Ellipse instances
  selection: SelectionModel<EllipseDB> = new (SelectionModel)
  initialSelection = new Array<EllipseDB>()

  // the data source for the table
  ellipses: EllipseDB[] = []
  matTableDataSource: MatTableDataSource<EllipseDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.ellipses
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (ellipseDB: EllipseDB, property: string) => {
      switch (property) {
        case 'ID':
          return ellipseDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return ellipseDB.Name;

        case 'CX':
          return ellipseDB.CX;

        case 'CY':
          return ellipseDB.CY;

        case 'RX':
          return ellipseDB.RX;

        case 'RY':
          return ellipseDB.RY;

        case 'Color':
          return ellipseDB.Color;

        case 'FillOpacity':
          return ellipseDB.FillOpacity;

        case 'Stroke':
          return ellipseDB.Stroke;

        case 'StrokeWidth':
          return ellipseDB.StrokeWidth;

        case 'StrokeDashArray':
          return ellipseDB.StrokeDashArray;

        case 'StrokeDashArrayWhenSelected':
          return ellipseDB.StrokeDashArrayWhenSelected;

        case 'Transform':
          return ellipseDB.Transform;

        case 'Layer_Ellipses':
          if (this.frontRepo.Layers.get(ellipseDB.Layer_EllipsesDBID.Int64) != undefined) {
            return this.frontRepo.Layers.get(ellipseDB.Layer_EllipsesDBID.Int64)!.Name
          } else {
            return ""
          }

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (ellipseDB: EllipseDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the ellipseDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += ellipseDB.Name.toLowerCase()
      mergedContent += ellipseDB.CX.toString()
      mergedContent += ellipseDB.CY.toString()
      mergedContent += ellipseDB.RX.toString()
      mergedContent += ellipseDB.RY.toString()
      mergedContent += ellipseDB.Color.toLowerCase()
      mergedContent += ellipseDB.FillOpacity.toString()
      mergedContent += ellipseDB.Stroke.toLowerCase()
      mergedContent += ellipseDB.StrokeWidth.toString()
      mergedContent += ellipseDB.StrokeDashArray.toLowerCase()
      mergedContent += ellipseDB.StrokeDashArrayWhenSelected.toLowerCase()
      mergedContent += ellipseDB.Transform.toLowerCase()
      if (ellipseDB.Layer_EllipsesDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Layers.get(ellipseDB.Layer_EllipsesDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private ellipseService: EllipseService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of ellipse instances
    public dialogRef: MatDialogRef<EllipsesTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.ellipseService.EllipseServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getEllipses()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "CX",
        "CY",
        "RX",
        "RY",
        "Color",
        "FillOpacity",
        "Stroke",
        "StrokeWidth",
        "StrokeDashArray",
        "StrokeDashArrayWhenSelected",
        "Transform",
        "Layer_Ellipses",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "CX",
        "CY",
        "RX",
        "RY",
        "Color",
        "FillOpacity",
        "Stroke",
        "StrokeWidth",
        "StrokeDashArray",
        "StrokeDashArrayWhenSelected",
        "Transform",
        "Layer_Ellipses",
      ]
      this.selection = new SelectionModel<EllipseDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getEllipses()

    this.matTableDataSource = new MatTableDataSource(this.ellipses)
  }

  getEllipses(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.ellipses = this.frontRepo.Ellipses_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let ellipse of this.ellipses) {
            let ID = this.dialogData.ID
            let revPointer = ellipse[this.dialogData.ReversePointer as keyof EllipseDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(ellipse)
            }
            this.selection = new SelectionModel<EllipseDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, EllipseDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to EllipseDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as EllipseDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let ellipse = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as EllipseDB
              this.initialSelection.push(ellipse)
            }
          }

          this.selection = new SelectionModel<EllipseDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.ellipses
      }
    )
  }

  // newEllipse initiate a new ellipse
  // create a new Ellipse objet
  newEllipse() {
  }

  deleteEllipse(ellipseID: number, ellipse: EllipseDB) {
    // list of ellipses is truncated of ellipse before the delete
    this.ellipses = this.ellipses.filter(h => h !== ellipse);

    this.ellipseService.deleteEllipse(ellipseID, this.GONG__StackPath).subscribe(
      ellipse => {
        this.ellipseService.EllipseServiceChanged.next("delete")
      }
    );
  }

  editEllipse(ellipseID: number, ellipse: EllipseDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(ellipseID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "ellipse" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, ellipseID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.ellipses.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.ellipses.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<EllipseDB>()

      // reset all initial selection of ellipse that belong to ellipse
      for (let ellipse of this.initialSelection) {
        let index = ellipse[this.dialogData.ReversePointer as keyof EllipseDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(ellipse)

      }

      // from selection, set ellipse that belong to ellipse
      for (let ellipse of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = ellipse[this.dialogData.ReversePointer as keyof EllipseDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(ellipse)
      }


      // update all ellipse (only update selection & initial selection)
      for (let ellipse of toUpdate) {
        this.ellipseService.updateEllipse(ellipse, this.GONG__StackPath)
          .subscribe(ellipse => {
            this.ellipseService.EllipseServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, EllipseDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedEllipse = new Set<number>()
      for (let ellipse of this.initialSelection) {
        if (this.selection.selected.includes(ellipse)) {
          // console.log("ellipse " + ellipse.Name + " is still selected")
        } else {
          console.log("ellipse " + ellipse.Name + " has been unselected")
          unselectedEllipse.add(ellipse.ID)
          console.log("is unselected " + unselectedEllipse.has(ellipse.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let ellipse = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as EllipseDB
      if (unselectedEllipse.has(ellipse.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<EllipseDB>) = new Array<EllipseDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          ellipse => {
            if (!this.initialSelection.includes(ellipse)) {
              // console.log("ellipse " + ellipse.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + ellipse.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = ellipse.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = ellipse.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("ellipse " + ellipse.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<EllipseDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
