// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { LineDB } from '../line-db'
import { LineService } from '../line.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-linestable',
  templateUrl: './lines-table.component.html',
  styleUrls: ['./lines-table.component.css'],
})
export class LinesTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of Line instances
  selection: SelectionModel<LineDB> = new (SelectionModel)
  initialSelection = new Array<LineDB>()

  // the data source for the table
  lines: LineDB[] = []
  matTableDataSource: MatTableDataSource<LineDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.lines
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (lineDB: LineDB, property: string) => {
      switch (property) {
        case 'ID':
          return lineDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return lineDB.Name;

        case 'X1':
          return lineDB.X1;

        case 'Y1':
          return lineDB.Y1;

        case 'X2':
          return lineDB.X2;

        case 'Y2':
          return lineDB.Y2;

        case 'Color':
          return lineDB.Color;

        case 'FillOpacity':
          return lineDB.FillOpacity;

        case 'Stroke':
          return lineDB.Stroke;

        case 'StrokeWidth':
          return lineDB.StrokeWidth;

        case 'StrokeDashArray':
          return lineDB.StrokeDashArray;

        case 'StrokeDashArrayWhenSelected':
          return lineDB.StrokeDashArrayWhenSelected;

        case 'Transform':
          return lineDB.Transform;

        case 'MouseClickX':
          return lineDB.MouseClickX;

        case 'MouseClickY':
          return lineDB.MouseClickY;

        case 'Layer_Lines':
          if (this.frontRepo.Layers.get(lineDB.Layer_LinesDBID.Int64) != undefined) {
            return this.frontRepo.Layers.get(lineDB.Layer_LinesDBID.Int64)!.Name
          } else {
            return ""
          }

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (lineDB: LineDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the lineDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += lineDB.Name.toLowerCase()
      mergedContent += lineDB.X1.toString()
      mergedContent += lineDB.Y1.toString()
      mergedContent += lineDB.X2.toString()
      mergedContent += lineDB.Y2.toString()
      mergedContent += lineDB.Color.toLowerCase()
      mergedContent += lineDB.FillOpacity.toString()
      mergedContent += lineDB.Stroke.toLowerCase()
      mergedContent += lineDB.StrokeWidth.toString()
      mergedContent += lineDB.StrokeDashArray.toLowerCase()
      mergedContent += lineDB.StrokeDashArrayWhenSelected.toLowerCase()
      mergedContent += lineDB.Transform.toLowerCase()
      mergedContent += lineDB.MouseClickX.toString()
      mergedContent += lineDB.MouseClickY.toString()
      if (lineDB.Layer_LinesDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Layers.get(lineDB.Layer_LinesDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private lineService: LineService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of line instances
    public dialogRef: MatDialogRef<LinesTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.lineService.LineServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getLines()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "X1",
        "Y1",
        "X2",
        "Y2",
        "Color",
        "FillOpacity",
        "Stroke",
        "StrokeWidth",
        "StrokeDashArray",
        "StrokeDashArrayWhenSelected",
        "Transform",
        "MouseClickX",
        "MouseClickY",
        "Layer_Lines",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "X1",
        "Y1",
        "X2",
        "Y2",
        "Color",
        "FillOpacity",
        "Stroke",
        "StrokeWidth",
        "StrokeDashArray",
        "StrokeDashArrayWhenSelected",
        "Transform",
        "MouseClickX",
        "MouseClickY",
        "Layer_Lines",
      ]
      this.selection = new SelectionModel<LineDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getLines()

    this.matTableDataSource = new MatTableDataSource(this.lines)
  }

  getLines(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.lines = this.frontRepo.Lines_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let line of this.lines) {
            let ID = this.dialogData.ID
            let revPointer = line[this.dialogData.ReversePointer as keyof LineDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(line)
            }
            this.selection = new SelectionModel<LineDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, LineDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to LineDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as LineDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let line = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as LineDB
              this.initialSelection.push(line)
            }
          }

          this.selection = new SelectionModel<LineDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.lines
      }
    )
  }

  // newLine initiate a new line
  // create a new Line objet
  newLine() {
  }

  deleteLine(lineID: number, line: LineDB) {
    // list of lines is truncated of line before the delete
    this.lines = this.lines.filter(h => h !== line);

    this.lineService.deleteLine(lineID, this.GONG__StackPath).subscribe(
      line => {
        this.lineService.LineServiceChanged.next("delete")
      }
    );
  }

  editLine(lineID: number, line: LineDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(lineID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "line" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, lineID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.lines.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.lines.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<LineDB>()

      // reset all initial selection of line that belong to line
      for (let line of this.initialSelection) {
        let index = line[this.dialogData.ReversePointer as keyof LineDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(line)

      }

      // from selection, set line that belong to line
      for (let line of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = line[this.dialogData.ReversePointer as keyof LineDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(line)
      }


      // update all line (only update selection & initial selection)
      for (let line of toUpdate) {
        this.lineService.updateLine(line, this.GONG__StackPath)
          .subscribe(line => {
            this.lineService.LineServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, LineDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedLine = new Set<number>()
      for (let line of this.initialSelection) {
        if (this.selection.selected.includes(line)) {
          // console.log("line " + line.Name + " is still selected")
        } else {
          console.log("line " + line.Name + " has been unselected")
          unselectedLine.add(line.ID)
          console.log("is unselected " + unselectedLine.has(line.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let line = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as LineDB
      if (unselectedLine.has(line.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<LineDB>) = new Array<LineDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          line => {
            if (!this.initialSelection.includes(line)) {
              // console.log("line " + line.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + line.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = line.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = line.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("line " + line.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<LineDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
