// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { PathDB } from '../path-db'
import { PathService } from '../path.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-pathstable',
  templateUrl: './paths-table.component.html',
  styleUrls: ['./paths-table.component.css'],
})
export class PathsTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of Path instances
  selection: SelectionModel<PathDB> = new (SelectionModel)
  initialSelection = new Array<PathDB>()

  // the data source for the table
  paths: PathDB[] = []
  matTableDataSource: MatTableDataSource<PathDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.paths
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (pathDB: PathDB, property: string) => {
      switch (property) {
        case 'ID':
          return pathDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return pathDB.Name;

        case 'Definition':
          return pathDB.Definition;

        case 'Color':
          return pathDB.Color;

        case 'FillOpacity':
          return pathDB.FillOpacity;

        case 'Stroke':
          return pathDB.Stroke;

        case 'StrokeWidth':
          return pathDB.StrokeWidth;

        case 'StrokeDashArray':
          return pathDB.StrokeDashArray;

        case 'StrokeDashArrayWhenSelected':
          return pathDB.StrokeDashArrayWhenSelected;

        case 'Transform':
          return pathDB.Transform;

        case 'Layer_Paths':
          if (this.frontRepo.Layers.get(pathDB.Layer_PathsDBID.Int64) != undefined) {
            return this.frontRepo.Layers.get(pathDB.Layer_PathsDBID.Int64)!.Name
          } else {
            return ""
          }

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (pathDB: PathDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the pathDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += pathDB.Name.toLowerCase()
      mergedContent += pathDB.Definition.toLowerCase()
      mergedContent += pathDB.Color.toLowerCase()
      mergedContent += pathDB.FillOpacity.toString()
      mergedContent += pathDB.Stroke.toLowerCase()
      mergedContent += pathDB.StrokeWidth.toString()
      mergedContent += pathDB.StrokeDashArray.toLowerCase()
      mergedContent += pathDB.StrokeDashArrayWhenSelected.toLowerCase()
      mergedContent += pathDB.Transform.toLowerCase()
      if (pathDB.Layer_PathsDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Layers.get(pathDB.Layer_PathsDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private pathService: PathService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of path instances
    public dialogRef: MatDialogRef<PathsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.pathService.PathServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getPaths()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Definition",
        "Color",
        "FillOpacity",
        "Stroke",
        "StrokeWidth",
        "StrokeDashArray",
        "StrokeDashArrayWhenSelected",
        "Transform",
        "Layer_Paths",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Definition",
        "Color",
        "FillOpacity",
        "Stroke",
        "StrokeWidth",
        "StrokeDashArray",
        "StrokeDashArrayWhenSelected",
        "Transform",
        "Layer_Paths",
      ]
      this.selection = new SelectionModel<PathDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getPaths()

    this.matTableDataSource = new MatTableDataSource(this.paths)
  }

  getPaths(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.paths = this.frontRepo.Paths_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let path of this.paths) {
            let ID = this.dialogData.ID
            let revPointer = path[this.dialogData.ReversePointer as keyof PathDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(path)
            }
            this.selection = new SelectionModel<PathDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, PathDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to PathDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as PathDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let path = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as PathDB
              this.initialSelection.push(path)
            }
          }

          this.selection = new SelectionModel<PathDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.paths
      }
    )
  }

  // newPath initiate a new path
  // create a new Path objet
  newPath() {
  }

  deletePath(pathID: number, path: PathDB) {
    // list of paths is truncated of path before the delete
    this.paths = this.paths.filter(h => h !== path);

    this.pathService.deletePath(pathID, this.GONG__StackPath).subscribe(
      path => {
        this.pathService.PathServiceChanged.next("delete")
      }
    );
  }

  editPath(pathID: number, path: PathDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(pathID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "path" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, pathID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.paths.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.paths.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<PathDB>()

      // reset all initial selection of path that belong to path
      for (let path of this.initialSelection) {
        let index = path[this.dialogData.ReversePointer as keyof PathDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(path)

      }

      // from selection, set path that belong to path
      for (let path of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = path[this.dialogData.ReversePointer as keyof PathDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(path)
      }


      // update all path (only update selection & initial selection)
      for (let path of toUpdate) {
        this.pathService.updatePath(path, this.GONG__StackPath)
          .subscribe(path => {
            this.pathService.PathServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, PathDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedPath = new Set<number>()
      for (let path of this.initialSelection) {
        if (this.selection.selected.includes(path)) {
          // console.log("path " + path.Name + " is still selected")
        } else {
          console.log("path " + path.Name + " has been unselected")
          unselectedPath.add(path.ID)
          console.log("is unselected " + unselectedPath.has(path.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let path = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as PathDB
      if (unselectedPath.has(path.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<PathDB>) = new Array<PathDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          path => {
            if (!this.initialSelection.includes(path)) {
              // console.log("path " + path.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + path.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = path.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = path.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("path " + path.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<PathDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
