import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { PolygoneDB } from '../polygone-db'
import { PolygoneService } from '../polygone.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'
import { NullInt64 } from '../null-int64'

@Component({
  selector: 'lib-polygone-sorting',
  templateUrl: './polygone-sorting.component.html',
  styleUrls: ['./polygone-sorting.component.css']
})
export class PolygoneSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of Polygone instances that are in the association
  associatedPolygones = new Array<PolygoneDB>();

  constructor(
    private polygoneService: PolygoneService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of polygone instances
    public dialogRef: MatDialogRef<PolygoneSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getPolygones()
  }

  getPolygones(): void {
    this.frontRepoService.pull(this.dialogData.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let polygone of this.frontRepo.Polygones_array) {
          let ID = this.dialogData.ID
          let revPointerID = polygone[this.dialogData.ReversePointer as keyof PolygoneDB] as unknown as NullInt64
          let revPointerID_Index = polygone[this.dialogData.ReversePointer + "_Index" as keyof PolygoneDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedPolygones.push(polygone)
          }
        }

        // sort associated polygone according to order
        this.associatedPolygones.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedPolygones, event.previousIndex, event.currentIndex);

    // set the order of Polygone instances
    let index = 0

    for (let polygone of this.associatedPolygones) {
      let revPointerID_Index = polygone[this.dialogData.ReversePointer + "_Index" as keyof PolygoneDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedPolygones.forEach(
      polygone => {
        this.polygoneService.updatePolygone(polygone, this.dialogData.GONG__StackPath)
          .subscribe(polygone => {
            this.polygoneService.PolygoneServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer + ' done');
  }
}
