import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { RectAnchoredTextDB } from '../rectanchoredtext-db'
import { RectAnchoredTextService } from '../rectanchoredtext.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-rectanchoredtextstable',
  templateUrl: './rectanchoredtexts-table.component.html',
  styleUrls: ['./rectanchoredtexts-table.component.css'],
})
export class RectAnchoredTextsTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of RectAnchoredText instances
  selection: SelectionModel<RectAnchoredTextDB> = new (SelectionModel)
  initialSelection = new Array<RectAnchoredTextDB>()

  // the data source for the table
  rectanchoredtexts: RectAnchoredTextDB[] = []
  matTableDataSource: MatTableDataSource<RectAnchoredTextDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.rectanchoredtexts
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (rectanchoredtextDB: RectAnchoredTextDB, property: string) => {
      switch (property) {
        case 'ID':
          return rectanchoredtextDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return rectanchoredtextDB.Name;

        case 'Content':
          return rectanchoredtextDB.Content;

        case 'FontWeight':
          return rectanchoredtextDB.FontWeight;

        case 'FontSize':
          return rectanchoredtextDB.FontSize;

        case 'X_Offset':
          return rectanchoredtextDB.X_Offset;

        case 'Y_Offset':
          return rectanchoredtextDB.Y_Offset;

        case 'RectAnchorType':
          return rectanchoredtextDB.RectAnchorType;

        case 'TextAnchorType':
          return rectanchoredtextDB.TextAnchorType;

        case 'Color':
          return rectanchoredtextDB.Color;

        case 'FillOpacity':
          return rectanchoredtextDB.FillOpacity;

        case 'Stroke':
          return rectanchoredtextDB.Stroke;

        case 'StrokeWidth':
          return rectanchoredtextDB.StrokeWidth;

        case 'StrokeDashArray':
          return rectanchoredtextDB.StrokeDashArray;

        case 'StrokeDashArrayWhenSelected':
          return rectanchoredtextDB.StrokeDashArrayWhenSelected;

        case 'Transform':
          return rectanchoredtextDB.Transform;

        case 'Rect_RectAnchoredTexts':
          if (this.frontRepo.Rects.get(rectanchoredtextDB.Rect_RectAnchoredTextsDBID.Int64) != undefined) {
            return this.frontRepo.Rects.get(rectanchoredtextDB.Rect_RectAnchoredTextsDBID.Int64)!.Name
          } else {
            return ""
          }

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (rectanchoredtextDB: RectAnchoredTextDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the rectanchoredtextDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += rectanchoredtextDB.Name.toLowerCase()
      mergedContent += rectanchoredtextDB.Content.toLowerCase()
      mergedContent += rectanchoredtextDB.FontWeight.toLowerCase()
      mergedContent += rectanchoredtextDB.FontSize.toString()
      mergedContent += rectanchoredtextDB.X_Offset.toString()
      mergedContent += rectanchoredtextDB.Y_Offset.toString()
      mergedContent += rectanchoredtextDB.RectAnchorType.toLowerCase()
      mergedContent += rectanchoredtextDB.TextAnchorType.toLowerCase()
      mergedContent += rectanchoredtextDB.Color.toLowerCase()
      mergedContent += rectanchoredtextDB.FillOpacity.toString()
      mergedContent += rectanchoredtextDB.Stroke.toLowerCase()
      mergedContent += rectanchoredtextDB.StrokeWidth.toString()
      mergedContent += rectanchoredtextDB.StrokeDashArray.toLowerCase()
      mergedContent += rectanchoredtextDB.StrokeDashArrayWhenSelected.toLowerCase()
      mergedContent += rectanchoredtextDB.Transform.toLowerCase()
      if (rectanchoredtextDB.Rect_RectAnchoredTextsDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Rects.get(rectanchoredtextDB.Rect_RectAnchoredTextsDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private rectanchoredtextService: RectAnchoredTextService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of rectanchoredtext instances
    public dialogRef: MatDialogRef<RectAnchoredTextsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.rectanchoredtextService.RectAnchoredTextServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getRectAnchoredTexts()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Content",
        "FontWeight",
        "FontSize",
        "X_Offset",
        "Y_Offset",
        "RectAnchorType",
        "TextAnchorType",
        "Color",
        "FillOpacity",
        "Stroke",
        "StrokeWidth",
        "StrokeDashArray",
        "StrokeDashArrayWhenSelected",
        "Transform",
        "Rect_RectAnchoredTexts",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Content",
        "FontWeight",
        "FontSize",
        "X_Offset",
        "Y_Offset",
        "RectAnchorType",
        "TextAnchorType",
        "Color",
        "FillOpacity",
        "Stroke",
        "StrokeWidth",
        "StrokeDashArray",
        "StrokeDashArrayWhenSelected",
        "Transform",
        "Rect_RectAnchoredTexts",
      ]
      this.selection = new SelectionModel<RectAnchoredTextDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getRectAnchoredTexts()

    this.matTableDataSource = new MatTableDataSource(this.rectanchoredtexts)
  }

  getRectAnchoredTexts(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.rectanchoredtexts = this.frontRepo.RectAnchoredTexts_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let rectanchoredtext of this.rectanchoredtexts) {
            let ID = this.dialogData.ID
            let revPointer = rectanchoredtext[this.dialogData.ReversePointer as keyof RectAnchoredTextDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(rectanchoredtext)
            }
            this.selection = new SelectionModel<RectAnchoredTextDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, RectAnchoredTextDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to RectAnchoredTextDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as RectAnchoredTextDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let rectanchoredtext = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as RectAnchoredTextDB
              this.initialSelection.push(rectanchoredtext)
            }
          }

          this.selection = new SelectionModel<RectAnchoredTextDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.rectanchoredtexts
      }
    )
  }

  // newRectAnchoredText initiate a new rectanchoredtext
  // create a new RectAnchoredText objet
  newRectAnchoredText() {
  }

  deleteRectAnchoredText(rectanchoredtextID: number, rectanchoredtext: RectAnchoredTextDB) {
    // list of rectanchoredtexts is truncated of rectanchoredtext before the delete
    this.rectanchoredtexts = this.rectanchoredtexts.filter(h => h !== rectanchoredtext);

    this.rectanchoredtextService.deleteRectAnchoredText(rectanchoredtextID, this.GONG__StackPath).subscribe(
      rectanchoredtext => {
        this.rectanchoredtextService.RectAnchoredTextServiceChanged.next("delete")
      }
    );
  }

  editRectAnchoredText(rectanchoredtextID: number, rectanchoredtext: RectAnchoredTextDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(rectanchoredtextID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "rectanchoredtext" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, rectanchoredtextID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.rectanchoredtexts.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.rectanchoredtexts.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<RectAnchoredTextDB>()

      // reset all initial selection of rectanchoredtext that belong to rectanchoredtext
      for (let rectanchoredtext of this.initialSelection) {
        let index = rectanchoredtext[this.dialogData.ReversePointer as keyof RectAnchoredTextDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(rectanchoredtext)

      }

      // from selection, set rectanchoredtext that belong to rectanchoredtext
      for (let rectanchoredtext of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = rectanchoredtext[this.dialogData.ReversePointer as keyof RectAnchoredTextDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(rectanchoredtext)
      }


      // update all rectanchoredtext (only update selection & initial selection)
      for (let rectanchoredtext of toUpdate) {
        this.rectanchoredtextService.updateRectAnchoredText(rectanchoredtext, this.GONG__StackPath)
          .subscribe(rectanchoredtext => {
            this.rectanchoredtextService.RectAnchoredTextServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, RectAnchoredTextDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedRectAnchoredText = new Set<number>()
      for (let rectanchoredtext of this.initialSelection) {
        if (this.selection.selected.includes(rectanchoredtext)) {
          // console.log("rectanchoredtext " + rectanchoredtext.Name + " is still selected")
        } else {
          console.log("rectanchoredtext " + rectanchoredtext.Name + " has been unselected")
          unselectedRectAnchoredText.add(rectanchoredtext.ID)
          console.log("is unselected " + unselectedRectAnchoredText.has(rectanchoredtext.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let rectanchoredtext = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as RectAnchoredTextDB
      if (unselectedRectAnchoredText.has(rectanchoredtext.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<RectAnchoredTextDB>) = new Array<RectAnchoredTextDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          rectanchoredtext => {
            if (!this.initialSelection.includes(rectanchoredtext)) {
              // console.log("rectanchoredtext " + rectanchoredtext.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + rectanchoredtext.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = rectanchoredtext.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = rectanchoredtext.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("rectanchoredtext " + rectanchoredtext.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<RectAnchoredTextDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
