// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-rectlinklinkstable',
  templateUrl: './rectlinklinks-table.component.html',
  styleUrls: ['./rectlinklinks-table.component.css'],
})
export class RectLinkLinksTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of RectLinkLink instances
  selection: SelectionModel<RectLinkLinkDB> = new (SelectionModel)
  initialSelection = new Array<RectLinkLinkDB>()

  // the data source for the table
  rectlinklinks: RectLinkLinkDB[] = []
  matTableDataSource: MatTableDataSource<RectLinkLinkDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.rectlinklinks
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (rectlinklinkDB: RectLinkLinkDB, property: string) => {
      switch (property) {
        case 'ID':
          return rectlinklinkDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return rectlinklinkDB.Name;

        case 'Start':
          return (rectlinklinkDB.Start ? rectlinklinkDB.Start.Name : '');

        case 'End':
          return (rectlinklinkDB.End ? rectlinklinkDB.End.Name : '');

        case 'TargetAnchorPosition':
          return rectlinklinkDB.TargetAnchorPosition;

        case 'Color':
          return rectlinklinkDB.Color;

        case 'FillOpacity':
          return rectlinklinkDB.FillOpacity;

        case 'Stroke':
          return rectlinklinkDB.Stroke;

        case 'StrokeWidth':
          return rectlinklinkDB.StrokeWidth;

        case 'StrokeDashArray':
          return rectlinklinkDB.StrokeDashArray;

        case 'StrokeDashArrayWhenSelected':
          return rectlinklinkDB.StrokeDashArrayWhenSelected;

        case 'Transform':
          return rectlinklinkDB.Transform;

        case 'Layer_RectLinkLinks':
          if (this.frontRepo.Layers.get(rectlinklinkDB.Layer_RectLinkLinksDBID.Int64) != undefined) {
            return this.frontRepo.Layers.get(rectlinklinkDB.Layer_RectLinkLinksDBID.Int64)!.Name
          } else {
            return ""
          }

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (rectlinklinkDB: RectLinkLinkDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the rectlinklinkDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += rectlinklinkDB.Name.toLowerCase()
      if (rectlinklinkDB.Start) {
        mergedContent += rectlinklinkDB.Start.Name.toLowerCase()
      }
      if (rectlinklinkDB.End) {
        mergedContent += rectlinklinkDB.End.Name.toLowerCase()
      }
      mergedContent += rectlinklinkDB.TargetAnchorPosition.toString()
      mergedContent += rectlinklinkDB.Color.toLowerCase()
      mergedContent += rectlinklinkDB.FillOpacity.toString()
      mergedContent += rectlinklinkDB.Stroke.toLowerCase()
      mergedContent += rectlinklinkDB.StrokeWidth.toString()
      mergedContent += rectlinklinkDB.StrokeDashArray.toLowerCase()
      mergedContent += rectlinklinkDB.StrokeDashArrayWhenSelected.toLowerCase()
      mergedContent += rectlinklinkDB.Transform.toLowerCase()
      if (rectlinklinkDB.Layer_RectLinkLinksDBID.Int64 != 0) {
        mergedContent += this.frontRepo.Layers.get(rectlinklinkDB.Layer_RectLinkLinksDBID.Int64)!.Name.toLowerCase()
      }


      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private rectlinklinkService: RectLinkLinkService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of rectlinklink instances
    public dialogRef: MatDialogRef<RectLinkLinksTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.rectlinklinkService.RectLinkLinkServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getRectLinkLinks()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Start",
        "End",
        "TargetAnchorPosition",
        "Color",
        "FillOpacity",
        "Stroke",
        "StrokeWidth",
        "StrokeDashArray",
        "StrokeDashArrayWhenSelected",
        "Transform",
        "Layer_RectLinkLinks",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Start",
        "End",
        "TargetAnchorPosition",
        "Color",
        "FillOpacity",
        "Stroke",
        "StrokeWidth",
        "StrokeDashArray",
        "StrokeDashArrayWhenSelected",
        "Transform",
        "Layer_RectLinkLinks",
      ]
      this.selection = new SelectionModel<RectLinkLinkDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getRectLinkLinks()

    this.matTableDataSource = new MatTableDataSource(this.rectlinklinks)
  }

  getRectLinkLinks(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.rectlinklinks = this.frontRepo.RectLinkLinks_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let rectlinklink of this.rectlinklinks) {
            let ID = this.dialogData.ID
            let revPointer = rectlinklink[this.dialogData.ReversePointer as keyof RectLinkLinkDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(rectlinklink)
            }
            this.selection = new SelectionModel<RectLinkLinkDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, RectLinkLinkDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to RectLinkLinkDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as RectLinkLinkDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let rectlinklink = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as RectLinkLinkDB
              this.initialSelection.push(rectlinklink)
            }
          }

          this.selection = new SelectionModel<RectLinkLinkDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.rectlinklinks
      }
    )
  }

  // newRectLinkLink initiate a new rectlinklink
  // create a new RectLinkLink objet
  newRectLinkLink() {
  }

  deleteRectLinkLink(rectlinklinkID: number, rectlinklink: RectLinkLinkDB) {
    // list of rectlinklinks is truncated of rectlinklink before the delete
    this.rectlinklinks = this.rectlinklinks.filter(h => h !== rectlinklink);

    this.rectlinklinkService.deleteRectLinkLink(rectlinklinkID, this.GONG__StackPath).subscribe(
      rectlinklink => {
        this.rectlinklinkService.RectLinkLinkServiceChanged.next("delete")
      }
    );
  }

  editRectLinkLink(rectlinklinkID: number, rectlinklink: RectLinkLinkDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(rectlinklinkID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "rectlinklink" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, rectlinklinkID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.rectlinklinks.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.rectlinklinks.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<RectLinkLinkDB>()

      // reset all initial selection of rectlinklink that belong to rectlinklink
      for (let rectlinklink of this.initialSelection) {
        let index = rectlinklink[this.dialogData.ReversePointer as keyof RectLinkLinkDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(rectlinklink)

      }

      // from selection, set rectlinklink that belong to rectlinklink
      for (let rectlinklink of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = rectlinklink[this.dialogData.ReversePointer as keyof RectLinkLinkDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(rectlinklink)
      }


      // update all rectlinklink (only update selection & initial selection)
      for (let rectlinklink of toUpdate) {
        this.rectlinklinkService.updateRectLinkLink(rectlinklink, this.GONG__StackPath)
          .subscribe(rectlinklink => {
            this.rectlinklinkService.RectLinkLinkServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, RectLinkLinkDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedRectLinkLink = new Set<number>()
      for (let rectlinklink of this.initialSelection) {
        if (this.selection.selected.includes(rectlinklink)) {
          // console.log("rectlinklink " + rectlinklink.Name + " is still selected")
        } else {
          console.log("rectlinklink " + rectlinklink.Name + " has been unselected")
          unselectedRectLinkLink.add(rectlinklink.ID)
          console.log("is unselected " + unselectedRectLinkLink.has(rectlinklink.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let rectlinklink = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as RectLinkLinkDB
      if (unselectedRectLinkLink.has(rectlinklink.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<RectLinkLinkDB>) = new Array<RectLinkLinkDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          rectlinklink => {
            if (!this.initialSelection.includes(rectlinklink)) {
              // console.log("rectlinklink " + rectlinklink.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + rectlinklink.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = rectlinklink.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = rectlinklink.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("rectlinklink " + rectlinklink.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<RectLinkLinkDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
