import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { CellIntDB } from '../cellint-db'
import { CellIntService } from '../cellint.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'
import { NullInt64 } from '../null-int64'

@Component({
  selector: 'lib-cellint-sorting',
  templateUrl: './cellint-sorting.component.html',
  styleUrls: ['./cellint-sorting.component.css']
})
export class CellIntSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of CellInt instances that are in the association
  associatedCellInts = new Array<CellIntDB>();

  constructor(
    private cellintService: CellIntService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of cellint instances
    public dialogRef: MatDialogRef<CellIntSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getCellInts()
  }

  getCellInts(): void {
    this.frontRepoService.pull(this.dialogData.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let cellint of this.frontRepo.CellInts_array) {
          let ID = this.dialogData.ID
          let revPointerID = cellint[this.dialogData.ReversePointer as keyof CellIntDB] as unknown as NullInt64
          let revPointerID_Index = cellint[this.dialogData.ReversePointer + "_Index" as keyof CellIntDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedCellInts.push(cellint)
          }
        }

        // sort associated cellint according to order
        this.associatedCellInts.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedCellInts, event.previousIndex, event.currentIndex);

    // set the order of CellInt instances
    let index = 0

    for (let cellint of this.associatedCellInts) {
      let revPointerID_Index = cellint[this.dialogData.ReversePointer + "_Index" as keyof CellIntDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedCellInts.forEach(
      cellint => {
        this.cellintService.updateCellInt(cellint, this.dialogData.GONG__StackPath)
          .subscribe(cellint => {
            this.cellintService.CellIntServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer + ' done');
  }
}
