import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { CellStringDB } from '../cellstring-db'
import { CellStringService } from '../cellstring.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'
import { NullInt64 } from '../null-int64'

@Component({
  selector: 'lib-cellstring-sorting',
  templateUrl: './cellstring-sorting.component.html',
  styleUrls: ['./cellstring-sorting.component.css']
})
export class CellStringSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of CellString instances that are in the association
  associatedCellStrings = new Array<CellStringDB>();

  constructor(
    private cellstringService: CellStringService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of cellstring instances
    public dialogRef: MatDialogRef<CellStringSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getCellStrings()
  }

  getCellStrings(): void {
    this.frontRepoService.pull(this.dialogData.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let cellstring of this.frontRepo.CellStrings_array) {
          let ID = this.dialogData.ID
          let revPointerID = cellstring[this.dialogData.ReversePointer as keyof CellStringDB] as unknown as NullInt64
          let revPointerID_Index = cellstring[this.dialogData.ReversePointer + "_Index" as keyof CellStringDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedCellStrings.push(cellstring)
          }
        }

        // sort associated cellstring according to order
        this.associatedCellStrings.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedCellStrings, event.previousIndex, event.currentIndex);

    // set the order of CellString instances
    let index = 0

    for (let cellstring of this.associatedCellStrings) {
      let revPointerID_Index = cellstring[this.dialogData.ReversePointer + "_Index" as keyof CellStringDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedCellStrings.forEach(
      cellstring => {
        this.cellstringService.updateCellString(cellstring, this.dialogData.GONG__StackPath)
          .subscribe(cellstring => {
            this.cellstringService.CellStringServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer + ' done');
  }
}
