import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { FormFieldDateDB } from '../formfielddate-db'
import { FormFieldDateService } from '../formfielddate.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-formfielddatestable',
  templateUrl: './formfielddates-table.component.html',
  styleUrls: ['./formfielddates-table.component.css'],
})
export class FormFieldDatesTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of FormFieldDate instances
  selection: SelectionModel<FormFieldDateDB> = new (SelectionModel)
  initialSelection = new Array<FormFieldDateDB>()

  // the data source for the table
  formfielddates: FormFieldDateDB[] = []
  matTableDataSource: MatTableDataSource<FormFieldDateDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.formfielddates
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (formfielddateDB: FormFieldDateDB, property: string) => {
      switch (property) {
        case 'ID':
          return formfielddateDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return formfielddateDB.Name;

        case 'Value':
          return (new Date(formfielddateDB.Value)).getTime()

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (formfielddateDB: FormFieldDateDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the formfielddateDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += formfielddateDB.Name.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private formfielddateService: FormFieldDateService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of formfielddate instances
    public dialogRef: MatDialogRef<FormFieldDatesTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.formfielddateService.FormFieldDateServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getFormFieldDates()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Value",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Value",
      ]
      this.selection = new SelectionModel<FormFieldDateDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getFormFieldDates()

    this.matTableDataSource = new MatTableDataSource(this.formfielddates)
  }

  getFormFieldDates(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.formfielddates = this.frontRepo.FormFieldDates_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let formfielddate of this.formfielddates) {
            let ID = this.dialogData.ID
            let revPointer = formfielddate[this.dialogData.ReversePointer as keyof FormFieldDateDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(formfielddate)
            }
            this.selection = new SelectionModel<FormFieldDateDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, FormFieldDateDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to FormFieldDateDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as FormFieldDateDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let formfielddate = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as FormFieldDateDB
              this.initialSelection.push(formfielddate)
            }
          }

          this.selection = new SelectionModel<FormFieldDateDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.formfielddates
      }
    )
  }

  // newFormFieldDate initiate a new formfielddate
  // create a new FormFieldDate objet
  newFormFieldDate() {
  }

  deleteFormFieldDate(formfielddateID: number, formfielddate: FormFieldDateDB) {
    // list of formfielddates is truncated of formfielddate before the delete
    this.formfielddates = this.formfielddates.filter(h => h !== formfielddate);

    this.formfielddateService.deleteFormFieldDate(formfielddateID, this.GONG__StackPath).subscribe(
      formfielddate => {
        this.formfielddateService.FormFieldDateServiceChanged.next("delete")
      }
    );
  }

  editFormFieldDate(formfielddateID: number, formfielddate: FormFieldDateDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(formfielddateID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "formfielddate" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, formfielddateID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.formfielddates.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.formfielddates.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<FormFieldDateDB>()

      // reset all initial selection of formfielddate that belong to formfielddate
      for (let formfielddate of this.initialSelection) {
        let index = formfielddate[this.dialogData.ReversePointer as keyof FormFieldDateDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(formfielddate)

      }

      // from selection, set formfielddate that belong to formfielddate
      for (let formfielddate of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = formfielddate[this.dialogData.ReversePointer as keyof FormFieldDateDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(formfielddate)
      }


      // update all formfielddate (only update selection & initial selection)
      for (let formfielddate of toUpdate) {
        this.formfielddateService.updateFormFieldDate(formfielddate, this.GONG__StackPath)
          .subscribe(formfielddate => {
            this.formfielddateService.FormFieldDateServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, FormFieldDateDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedFormFieldDate = new Set<number>()
      for (let formfielddate of this.initialSelection) {
        if (this.selection.selected.includes(formfielddate)) {
          // console.log("formfielddate " + formfielddate.Name + " is still selected")
        } else {
          console.log("formfielddate " + formfielddate.Name + " has been unselected")
          unselectedFormFieldDate.add(formfielddate.ID)
          console.log("is unselected " + unselectedFormFieldDate.has(formfielddate.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let formfielddate = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as FormFieldDateDB
      if (unselectedFormFieldDate.has(formfielddate.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<FormFieldDateDB>) = new Array<FormFieldDateDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          formfielddate => {
            if (!this.initialSelection.includes(formfielddate)) {
              // console.log("formfielddate " + formfielddate.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + formfielddate.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = formfielddate.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = formfielddate.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("formfielddate " + formfielddate.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<FormFieldDateDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
