// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { FormFieldIntDB } from '../formfieldint-db'
import { FormFieldIntService } from '../formfieldint.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-formfieldintstable',
  templateUrl: './formfieldints-table.component.html',
  styleUrls: ['./formfieldints-table.component.css'],
})
export class FormFieldIntsTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of FormFieldInt instances
  selection: SelectionModel<FormFieldIntDB> = new (SelectionModel)
  initialSelection = new Array<FormFieldIntDB>()

  // the data source for the table
  formfieldints: FormFieldIntDB[] = []
  matTableDataSource: MatTableDataSource<FormFieldIntDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.formfieldints
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (formfieldintDB: FormFieldIntDB, property: string) => {
      switch (property) {
        case 'ID':
          return formfieldintDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return formfieldintDB.Name;

        case 'Value':
          return formfieldintDB.Value;

        case 'HasMinValidator':
          return formfieldintDB.HasMinValidator ? "true" : "false";

        case 'MinValue':
          return formfieldintDB.MinValue;

        case 'HasMaxValidator':
          return formfieldintDB.HasMaxValidator ? "true" : "false";

        case 'MaxValue':
          return formfieldintDB.MaxValue;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (formfieldintDB: FormFieldIntDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the formfieldintDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += formfieldintDB.Name.toLowerCase()
      mergedContent += formfieldintDB.Value.toString()
      mergedContent += formfieldintDB.MinValue.toString()
      mergedContent += formfieldintDB.MaxValue.toString()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private formfieldintService: FormFieldIntService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of formfieldint instances
    public dialogRef: MatDialogRef<FormFieldIntsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.formfieldintService.FormFieldIntServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getFormFieldInts()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Value",
        "HasMinValidator",
        "MinValue",
        "HasMaxValidator",
        "MaxValue",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Value",
        "HasMinValidator",
        "MinValue",
        "HasMaxValidator",
        "MaxValue",
      ]
      this.selection = new SelectionModel<FormFieldIntDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getFormFieldInts()

    this.matTableDataSource = new MatTableDataSource(this.formfieldints)
  }

  getFormFieldInts(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.formfieldints = this.frontRepo.FormFieldInts_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let formfieldint of this.formfieldints) {
            let ID = this.dialogData.ID
            let revPointer = formfieldint[this.dialogData.ReversePointer as keyof FormFieldIntDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(formfieldint)
            }
            this.selection = new SelectionModel<FormFieldIntDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, FormFieldIntDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to FormFieldIntDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as FormFieldIntDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let formfieldint = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as FormFieldIntDB
              this.initialSelection.push(formfieldint)
            }
          }

          this.selection = new SelectionModel<FormFieldIntDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.formfieldints
      }
    )
  }

  // newFormFieldInt initiate a new formfieldint
  // create a new FormFieldInt objet
  newFormFieldInt() {
  }

  deleteFormFieldInt(formfieldintID: number, formfieldint: FormFieldIntDB) {
    // list of formfieldints is truncated of formfieldint before the delete
    this.formfieldints = this.formfieldints.filter(h => h !== formfieldint);

    this.formfieldintService.deleteFormFieldInt(formfieldintID, this.GONG__StackPath).subscribe(
      formfieldint => {
        this.formfieldintService.FormFieldIntServiceChanged.next("delete")
      }
    );
  }

  editFormFieldInt(formfieldintID: number, formfieldint: FormFieldIntDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(formfieldintID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "formfieldint" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, formfieldintID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.formfieldints.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.formfieldints.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<FormFieldIntDB>()

      // reset all initial selection of formfieldint that belong to formfieldint
      for (let formfieldint of this.initialSelection) {
        let index = formfieldint[this.dialogData.ReversePointer as keyof FormFieldIntDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(formfieldint)

      }

      // from selection, set formfieldint that belong to formfieldint
      for (let formfieldint of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = formfieldint[this.dialogData.ReversePointer as keyof FormFieldIntDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(formfieldint)
      }


      // update all formfieldint (only update selection & initial selection)
      for (let formfieldint of toUpdate) {
        this.formfieldintService.updateFormFieldInt(formfieldint, this.GONG__StackPath)
          .subscribe(formfieldint => {
            this.formfieldintService.FormFieldIntServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, FormFieldIntDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedFormFieldInt = new Set<number>()
      for (let formfieldint of this.initialSelection) {
        if (this.selection.selected.includes(formfieldint)) {
          // console.log("formfieldint " + formfieldint.Name + " is still selected")
        } else {
          console.log("formfieldint " + formfieldint.Name + " has been unselected")
          unselectedFormFieldInt.add(formfieldint.ID)
          console.log("is unselected " + unselectedFormFieldInt.has(formfieldint.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let formfieldint = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as FormFieldIntDB
      if (unselectedFormFieldInt.has(formfieldint.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<FormFieldIntDB>) = new Array<FormFieldIntDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          formfieldint => {
            if (!this.initialSelection.includes(formfieldint)) {
              // console.log("formfieldint " + formfieldint.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + formfieldint.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = formfieldint.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = formfieldint.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("formfieldint " + formfieldint.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<FormFieldIntDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
