// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { FormFieldSelectDB } from '../formfieldselect-db'
import { FormFieldSelectService } from '../formfieldselect.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-formfieldselectstable',
  templateUrl: './formfieldselects-table.component.html',
  styleUrls: ['./formfieldselects-table.component.css'],
})
export class FormFieldSelectsTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of FormFieldSelect instances
  selection: SelectionModel<FormFieldSelectDB> = new (SelectionModel)
  initialSelection = new Array<FormFieldSelectDB>()

  // the data source for the table
  formfieldselects: FormFieldSelectDB[] = []
  matTableDataSource: MatTableDataSource<FormFieldSelectDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.formfieldselects
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (formfieldselectDB: FormFieldSelectDB, property: string) => {
      switch (property) {
        case 'ID':
          return formfieldselectDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return formfieldselectDB.Name;

        case 'Value':
          return (formfieldselectDB.Value ? formfieldselectDB.Value.Name : '');

        case 'CanBeEmpty':
          return formfieldselectDB.CanBeEmpty ? "true" : "false";

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (formfieldselectDB: FormFieldSelectDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the formfieldselectDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += formfieldselectDB.Name.toLowerCase()
      if (formfieldselectDB.Value) {
        mergedContent += formfieldselectDB.Value.Name.toLowerCase()
      }

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private formfieldselectService: FormFieldSelectService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of formfieldselect instances
    public dialogRef: MatDialogRef<FormFieldSelectsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.formfieldselectService.FormFieldSelectServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getFormFieldSelects()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Value",
        "CanBeEmpty",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Value",
        "CanBeEmpty",
      ]
      this.selection = new SelectionModel<FormFieldSelectDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getFormFieldSelects()

    this.matTableDataSource = new MatTableDataSource(this.formfieldselects)
  }

  getFormFieldSelects(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.formfieldselects = this.frontRepo.FormFieldSelects_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let formfieldselect of this.formfieldselects) {
            let ID = this.dialogData.ID
            let revPointer = formfieldselect[this.dialogData.ReversePointer as keyof FormFieldSelectDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(formfieldselect)
            }
            this.selection = new SelectionModel<FormFieldSelectDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, FormFieldSelectDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to FormFieldSelectDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as FormFieldSelectDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let formfieldselect = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as FormFieldSelectDB
              this.initialSelection.push(formfieldselect)
            }
          }

          this.selection = new SelectionModel<FormFieldSelectDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.formfieldselects
      }
    )
  }

  // newFormFieldSelect initiate a new formfieldselect
  // create a new FormFieldSelect objet
  newFormFieldSelect() {
  }

  deleteFormFieldSelect(formfieldselectID: number, formfieldselect: FormFieldSelectDB) {
    // list of formfieldselects is truncated of formfieldselect before the delete
    this.formfieldselects = this.formfieldselects.filter(h => h !== formfieldselect);

    this.formfieldselectService.deleteFormFieldSelect(formfieldselectID, this.GONG__StackPath).subscribe(
      formfieldselect => {
        this.formfieldselectService.FormFieldSelectServiceChanged.next("delete")
      }
    );
  }

  editFormFieldSelect(formfieldselectID: number, formfieldselect: FormFieldSelectDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(formfieldselectID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "formfieldselect" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, formfieldselectID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.formfieldselects.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.formfieldselects.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<FormFieldSelectDB>()

      // reset all initial selection of formfieldselect that belong to formfieldselect
      for (let formfieldselect of this.initialSelection) {
        let index = formfieldselect[this.dialogData.ReversePointer as keyof FormFieldSelectDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(formfieldselect)

      }

      // from selection, set formfieldselect that belong to formfieldselect
      for (let formfieldselect of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = formfieldselect[this.dialogData.ReversePointer as keyof FormFieldSelectDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(formfieldselect)
      }


      // update all formfieldselect (only update selection & initial selection)
      for (let formfieldselect of toUpdate) {
        this.formfieldselectService.updateFormFieldSelect(formfieldselect, this.GONG__StackPath)
          .subscribe(formfieldselect => {
            this.formfieldselectService.FormFieldSelectServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, FormFieldSelectDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedFormFieldSelect = new Set<number>()
      for (let formfieldselect of this.initialSelection) {
        if (this.selection.selected.includes(formfieldselect)) {
          // console.log("formfieldselect " + formfieldselect.Name + " is still selected")
        } else {
          console.log("formfieldselect " + formfieldselect.Name + " has been unselected")
          unselectedFormFieldSelect.add(formfieldselect.ID)
          console.log("is unselected " + unselectedFormFieldSelect.has(formfieldselect.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let formfieldselect = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as FormFieldSelectDB
      if (unselectedFormFieldSelect.has(formfieldselect.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<FormFieldSelectDB>) = new Array<FormFieldSelectDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          formfieldselect => {
            if (!this.initialSelection.includes(formfieldselect)) {
              // console.log("formfieldselect " + formfieldselect.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + formfieldselect.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = formfieldselect.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = formfieldselect.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("formfieldselect " + formfieldselect.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<FormFieldSelectDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
