// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { FormFieldStringDB } from '../formfieldstring-db'
import { FormFieldStringService } from '../formfieldstring.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-formfieldstringstable',
  templateUrl: './formfieldstrings-table.component.html',
  styleUrls: ['./formfieldstrings-table.component.css'],
})
export class FormFieldStringsTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of FormFieldString instances
  selection: SelectionModel<FormFieldStringDB> = new (SelectionModel)
  initialSelection = new Array<FormFieldStringDB>()

  // the data source for the table
  formfieldstrings: FormFieldStringDB[] = []
  matTableDataSource: MatTableDataSource<FormFieldStringDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.formfieldstrings
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (formfieldstringDB: FormFieldStringDB, property: string) => {
      switch (property) {
        case 'ID':
          return formfieldstringDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return formfieldstringDB.Name;

        case 'Value':
          return formfieldstringDB.Value;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (formfieldstringDB: FormFieldStringDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the formfieldstringDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += formfieldstringDB.Name.toLowerCase()
      mergedContent += formfieldstringDB.Value.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private formfieldstringService: FormFieldStringService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of formfieldstring instances
    public dialogRef: MatDialogRef<FormFieldStringsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.formfieldstringService.FormFieldStringServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getFormFieldStrings()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Value",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Value",
      ]
      this.selection = new SelectionModel<FormFieldStringDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getFormFieldStrings()

    this.matTableDataSource = new MatTableDataSource(this.formfieldstrings)
  }

  getFormFieldStrings(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.formfieldstrings = this.frontRepo.FormFieldStrings_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let formfieldstring of this.formfieldstrings) {
            let ID = this.dialogData.ID
            let revPointer = formfieldstring[this.dialogData.ReversePointer as keyof FormFieldStringDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(formfieldstring)
            }
            this.selection = new SelectionModel<FormFieldStringDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, FormFieldStringDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to FormFieldStringDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as FormFieldStringDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let formfieldstring = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as FormFieldStringDB
              this.initialSelection.push(formfieldstring)
            }
          }

          this.selection = new SelectionModel<FormFieldStringDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.formfieldstrings
      }
    )
  }

  // newFormFieldString initiate a new formfieldstring
  // create a new FormFieldString objet
  newFormFieldString() {
  }

  deleteFormFieldString(formfieldstringID: number, formfieldstring: FormFieldStringDB) {
    // list of formfieldstrings is truncated of formfieldstring before the delete
    this.formfieldstrings = this.formfieldstrings.filter(h => h !== formfieldstring);

    this.formfieldstringService.deleteFormFieldString(formfieldstringID, this.GONG__StackPath).subscribe(
      formfieldstring => {
        this.formfieldstringService.FormFieldStringServiceChanged.next("delete")
      }
    );
  }

  editFormFieldString(formfieldstringID: number, formfieldstring: FormFieldStringDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(formfieldstringID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "formfieldstring" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, formfieldstringID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.formfieldstrings.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.formfieldstrings.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<FormFieldStringDB>()

      // reset all initial selection of formfieldstring that belong to formfieldstring
      for (let formfieldstring of this.initialSelection) {
        let index = formfieldstring[this.dialogData.ReversePointer as keyof FormFieldStringDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(formfieldstring)

      }

      // from selection, set formfieldstring that belong to formfieldstring
      for (let formfieldstring of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = formfieldstring[this.dialogData.ReversePointer as keyof FormFieldStringDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(formfieldstring)
      }


      // update all formfieldstring (only update selection & initial selection)
      for (let formfieldstring of toUpdate) {
        this.formfieldstringService.updateFormFieldString(formfieldstring, this.GONG__StackPath)
          .subscribe(formfieldstring => {
            this.formfieldstringService.FormFieldStringServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, FormFieldStringDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedFormFieldString = new Set<number>()
      for (let formfieldstring of this.initialSelection) {
        if (this.selection.selected.includes(formfieldstring)) {
          // console.log("formfieldstring " + formfieldstring.Name + " is still selected")
        } else {
          console.log("formfieldstring " + formfieldstring.Name + " has been unselected")
          unselectedFormFieldString.add(formfieldstring.ID)
          console.log("is unselected " + unselectedFormFieldString.has(formfieldstring.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let formfieldstring = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as FormFieldStringDB
      if (unselectedFormFieldString.has(formfieldstring.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<FormFieldStringDB>) = new Array<FormFieldStringDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          formfieldstring => {
            if (!this.initialSelection.includes(formfieldstring)) {
              // console.log("formfieldstring " + formfieldstring.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + formfieldstring.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = formfieldstring.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = formfieldstring.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("formfieldstring " + formfieldstring.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<FormFieldStringDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
